import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const UserProfile = () => {
const navigate = useNavigate();
const [isLoggedIn, setIsLoggedIn] = useState(false);
const [loading, setLoading] = useState(true);
const [editing, setEditing] = useState(false);
const [updating, setUpdating] = useState(false);
const [userProfile, setUserProfile] = useState({
    name: "",
    email: "",
    phone: "",
    dateOfBirth: "",
    gender: "",
    address: {
    street: "",
    city: "",
    state: "",
    pincode: "",
    country: "India",
    },
    occupation: "",
    bio: "",
    birthPlace: "",
});

const [formData, setFormData] = useState(userProfile);

  // Check if user is logged in and fetch profile
useEffect(() => {
    const checkAuthAndFetchProfile = async () => {
    const token = localStorage.getItem("authToken");
    const userData = localStorage.getItem("userData");

    if (token && userData) {
        setIsLoggedIn(true);

        try {
          // Try to fetch fresh data from backend
          const response = await axios.get("http://localhost:4000/profile", {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          });

          if (response.data.user) {
            const backendUser = response.data.user;
            const profileData = {
              name: backendUser.name || "",
              email: backendUser.email || "",
              phone: "", // Not in backend schema
              dateOfBirth: backendUser.dateOfBirth || "",
              gender: backendUser.gender || "",
              address: {
                street: "",
                city: backendUser.birthPlace
                  ? backendUser.birthPlace.split(",")[0]?.trim()
                  : "",
                state: backendUser.birthPlace
                  ? backendUser.birthPlace.split(",")[1]?.trim()
                  : "",
                pincode: "",
                country: "India",
              },
              occupation: "",
              bio: "",
              birthPlace: backendUser.birthPlace || "",
            };

            setUserProfile(profileData);
            setFormData(profileData);

            // Update localStorage with fresh data
            localStorage.setItem("userData", JSON.stringify(profileData));
          }
        } catch (error) {
          console.error("Error fetching profile from backend:", error);

          // Fallback to localStorage data
          try {
            const localUserData = JSON.parse(userData);
            setUserProfile(localUserData);
            setFormData(localUserData);
          } catch (parseError) {
            console.error("Error parsing local user data:", parseError);
          }
        }
      } else {
        setIsLoggedIn(false);
      }
      setLoading(false);
    };

    checkAuthAndFetchProfile();
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;

    if (name.includes(".")) {
      const [parent, child] = name.split(".");
      setFormData((prev) => ({
        ...prev,
        [parent]: {
          ...prev[parent],
          [child]: value,
        },
      }));
    } else {
      setFormData((prev) => ({
        ...prev,
        [name]: value,
      }));
    }
  };

  const handleSave = async () => {
    setUpdating(true);
    const token = localStorage.getItem("authToken");

    try {
      // Prepare data for backend (matching your API structure)
      const updateData = {
        fullName: formData.name,
        dateOfBirth: formData.dateOfBirth,
        birthPlace:
          formData.birthPlace ||
          `${formData.address?.city || ""}, ${formData.address?.state || ""}`
            .trim()
            .replace(/^,|,$/, ""),
        gender: formData.gender,
      };

      const response = await axios.put(
        "http://localhost:4000/profile",
        updateData,
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      if (response.data.message) {
        setUserProfile(formData);
        localStorage.setItem("userData", JSON.stringify(formData));
        setEditing(false);
        alert(response.data.message);
      }
    } catch (error) {
      console.error("Error updating profile:", error);

      // Fallback to local storage update
      setUserProfile(formData);
      localStorage.setItem("userData", JSON.stringify(formData));
      setEditing(false);
      alert("Profile updated locally! (Backend update failed)");
    } finally {
      setUpdating(false);
    }
  };

  const handleCancel = () => {
    setFormData(userProfile);
    setEditing(false);
  };

  const handleLoginRedirect = () => {
    navigate("/login");
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-purple-900 via-blue-900 to-indigo-900">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-yellow-400 mx-auto"></div>
          <p className="mt-4 text-yellow-200">
            Loading your mystical profile...
          </p>
        </div>
      </div>
    );
  }

  if (!isLoggedIn) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-purple-900 via-blue-900 to-indigo-900">
        <div className="max-w-md w-full bg-white/10 backdrop-blur-lg rounded-2xl shadow-2xl p-8 text-center border border-white/20">
          <div className="mb-6">
            <div className="text-6xl mb-4">🔮</div>
          </div>
          <h2 className="text-3xl font-bold text-yellow-400 mb-4">
            Access Restricted
          </h2>
          <p className="text-white/80 mb-6">
            Please login first to access your mystical profile.
          </p>
          <button
            onClick={handleLoginRedirect}
            className="w-full bg-gradient-to-r from-yellow-400 to-orange-500 hover:from-yellow-500 hover:to-orange-600 text-black font-bold py-3 px-6 rounded-lg transition duration-300 transform hover:scale-105"
          >
            🌟 Login Now
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-900 via-blue-900 to-indigo-900 py-8">
      <div className="max-w-6xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="bg-white/10 backdrop-blur-lg rounded-2xl shadow-2xl overflow-hidden border border-white/20">
          {/* Header */}
          <div className="bg-gradient-to-r from-purple-600 via-pink-600 to-yellow-500 px-6 py-8">
            <div className="flex items-center justify-between">
              <div className="flex items-center space-x-4">
                <div className="h-20 w-20 bg-white/20 backdrop-blur rounded-full flex items-center justify-center border-2 border-white/30">
                  <span className="text-3xl font-bold text-white">
                    {userProfile.name
                      ? userProfile.name.charAt(0).toUpperCase()
                      : "🔮"}
                  </span>
                </div>
                <div>
                  <h1 className="text-4xl font-bold text-white drop-shadow-lg">
                    {userProfile.name || "Mystical Seeker"}
                  </h1>
                  <p className="text-white/80 text-lg">{userProfile.email}</p>
                  <p className="text-white/60">✨ NumPath Portal Member</p>
                </div>
              </div>
              <div>
                {!editing ? (
                  <button
                    onClick={() => setEditing(true)}
                    className="bg-white/20 backdrop-blur hover:bg-white/30 text-white px-6 py-3 rounded-xl font-medium transition duration-300 border border-white/30"
                  >
                    🖊️ Edit Profile
                  </button>
                ) : (
                  <div className="space-x-3">
                    <button
                      onClick={handleSave}
                      disabled={updating}
                      className="bg-green-500/80 backdrop-blur hover:bg-green-600/80 text-white px-6 py-3 rounded-xl font-medium transition duration-300 disabled:opacity-50"
                    >
                      {updating ? "⏳ Saving..." : "💾 Save"}
                    </button>
                    <button
                      onClick={handleCancel}
                      disabled={updating}
                      className="bg-red-500/80 backdrop-blur hover:bg-red-600/80 text-white px-6 py-3 rounded-xl font-medium transition duration-300 disabled:opacity-50"
                    >
                      ❌ Cancel
                    </button>
                  </div>
                )}
              </div>
            </div>
          </div>

          {/* Profile Content */}
          <div className="p-8">
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
              {/* Personal Information */}
              <div className="space-y-6">
                <h3 className="text-2xl font-bold text-yellow-400 border-b border-yellow-400/30 pb-3 flex items-center gap-2">
                  👤 Personal Information
                </h3>

                <div className="space-y-4">
                  <div>
                    <label className="block text-sm font-medium text-white/90 mb-2 flex items-center gap-2">
                      ✨ Full Name
                    </label>
                    {editing ? (
                      <input
                        type="text"
                        name="name"
                        value={formData.name}
                        onChange={handleInputChange}
                        className="w-full px-4 py-3 bg-white/10 backdrop-blur border border-white/30 rounded-xl text-white placeholder-white/50 focus:ring-2 focus:ring-yellow-400 focus:border-transparent transition duration-300"
                        placeholder="Enter your full name"
                      />
                    ) : (
                      <p className="text-white text-lg">
                        {userProfile.name || "Not provided"}
                      </p>
                    )}
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-white/90 mb-2 flex items-center gap-2">
                      📧 Email
                    </label>
                    <p className="text-white/80 text-lg">
                      {userProfile.email || "Not provided"}
                    </p>
                    <small className="text-white/50">
                      Email cannot be changed
                    </small>
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-white/90 mb-2 flex items-center gap-2">
                      📱 Phone Number
                    </label>
                    {editing ? (
                      <input
                        type="tel"
                        name="phone"
                        value={formData.phone}
                        onChange={handleInputChange}
                        className="w-full px-4 py-3 bg-white/10 backdrop-blur border border-white/30 rounded-xl text-white placeholder-white/50 focus:ring-2 focus:ring-yellow-400 focus:border-transparent transition duration-300"
                        placeholder="Enter your phone number"
                      />
                    ) : (
                      <p className="text-white text-lg">
                        {userProfile.phone || "Not provided"}
                      </p>
                    )}
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-white/90 mb-2 flex items-center gap-2">
                      🎂 Date of Birth
                    </label>
                    {editing ? (
                      <input
                        type="date"
                        name="dateOfBirth"
                        value={formData.dateOfBirth}
                        onChange={handleInputChange}
                        className="w-full px-4 py-3 bg-white/10 backdrop-blur border border-white/30 rounded-xl text-white focus:ring-2 focus:ring-yellow-400 focus:border-transparent transition duration-300"
                      />
                    ) : (
                      <p className="text-white text-lg">
                        {userProfile.dateOfBirth
                          ? new Date(
                              userProfile.dateOfBirth
                            ).toLocaleDateString("en-IN")
                          : "Not provided"}
                      </p>
                    )}
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-white/90 mb-2 flex items-center gap-2">
                      ⚧ Gender
                    </label>
                    {editing ? (
                      <select
                        name="gender"
                        value={formData.gender}
                        onChange={handleInputChange}
                        className="w-full px-4 py-3 bg-white/10 backdrop-blur border border-white/30 rounded-xl text-white focus:ring-2 focus:ring-yellow-400 focus:border-transparent transition duration-300"
                      >
                        <option value="" className="bg-gray-800">
                          Select Gender
                        </option>
                        <option value="male" className="bg-gray-800">
                          Male
                        </option>
                        <option value="female" className="bg-gray-800">
                          Female
                        </option>
                        <option value="other" className="bg-gray-800">
                          Other
                        </option>
                        <option
                          value="prefer-not-to-say"
                          className="bg-gray-800"
                        >
                          Prefer not to say
                        </option>
                      </select>
                    ) : (
                      <p className="text-white text-lg capitalize">
                        {userProfile.gender || "Not provided"}
                      </p>
                    )}
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-white/90 mb-2 flex items-center gap-2">
                      💼 Occupation
                    </label>
                    {editing ? (
                      <input
                        type="text"
                        name="occupation"
                        value={formData.occupation}
                        onChange={handleInputChange}
                        className="w-full px-4 py-3 bg-white/10 backdrop-blur border border-white/30 rounded-xl text-white placeholder-white/50 focus:ring-2 focus:ring-yellow-400 focus:border-transparent transition duration-300"
                        placeholder="Enter your occupation"
                      />
                    ) : (
                      <p className="text-white text-lg">
                        {userProfile.occupation || "Not provided"}
                      </p>
                    )}
                  </div>
                </div>
