// firebase.js
import { initializeApp } from "firebase/app";
import { getAuth, GoogleAuthProvider, signInWithPopup } from "firebase/auth";

const firebaseConfig = {
  apiKey: "AIzaSyCuF3hmLFAC60p4AKgqri5-JSWHitozRVE",
  authDomain: "numpath-1484f.firebaseapp.com",
  projectId: "numpath-1484f",
  storageBucket: "numpath-1484f.appspot.com",
  messagingSenderId: "457414546356",
  appId: "1:457414546356:web:8657afc2024651cb07d8e1",
  measurementId: "G-53QCBGFQY9"
};

// Initialize Firebase
const app = initializeApp(firebaseConfig);

// Auth export
export const auth = getAuth(app);
export const provider = new GoogleAuthProvider();

// Function for Google Sign-In
export const signInWithGoogle = () => {
  return signInWithPopup(auth, provider);
};
