import React, { useState, useEffect } from "react";
import {
  User,
  Edit,
  Save,
  X,
  LogOut,
  Mail,
  Phone,
  Calendar,
  MapPin,
  Camera,
  Lock,
  Trash2,
  Eye,
  EyeOff,
  AlertTriangle,
  CheckCircle,
} from "lucide-react";
import axios from "axios";

const UserProfile = () => {
  const [isEditing, setIsEditing] = useState(false);
  const [loading, setLoading] = useState(true);
  const [updating, setUpdating] = useState(false);
  const [uploadingImage, setUploadingImage] = useState(false);
  const [showPasswordModal, setShowPasswordModal] = useState(false);
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [showPassword, setShowPassword] = useState({
    current: false,
    new: false,
    confirm: false,
  });
  const [message, setMessage] = useState({ type: "", text: "" });

  const [userInfo, setUserInfo] = useState({
    name: "",
    email: "",
    phone: "",
    dateOfBirth: "",
    address: "",
    bio: "",
    profileImage: null,
    occupation: "",
    gender: "",
    birthPlace: "",
  });

  const [editedInfo, setEditedInfo] = useState({ ...userInfo });
  const [passwordData, setPasswordData] = useState({
    currentPassword: "",
    newPassword: "",
    confirmPassword: "",
  });
  const [deletePassword, setDeletePassword] = useState("");

  const API_BASE_URL = "http://localhost:4000";

  // Check authentication on component mount
  useEffect(() => {
    const token = localStorage.getItem("authToken");
    if (!token) {
      alert("Please login to access your profile");
      window.location.href = "/login";
      return;
    }
    fetchProfile();
  }, []);

  // Configure axios with auth token
  const getAuthHeaders = () => {
    const token = localStorage.getItem("authToken");
    return {
      headers: {
        Authorization: `Bearer ${token}`,
        "Content-Type": "application/json",
      },
    };
  };

  const showMessage = (type, text) => {
    setMessage({ type, text });
    setTimeout(() => setMessage({ type: "", text: "" }), 5000);
  };

  //
  //   try {
  //     setLoading(true);
  //     // Mock axios call since we don't have actual axios here
  //     // Replace with: const response = await axios.get(`${API_BASE_URL}/profile`, getAuthHeaders());
  //     const response = { data: { user: userInfo } };
  //     const userData = response.data.user;

  //     setUserInfo(userData);
  //     setEditedInfo(userData);
  //     setLoading(false);
  //   } catch (error) {
  //     console.error('Profile fetch error:', error);
  //     if (error.response?.status === 403) {
  //       alert('Session expired. Please login again.');
  //       localStorage.removeItem('authToken');
  //       window.location.href = '/login';
  //     } else {
  //       showMessage('error', 'Failed to load profile');
  //     }
  //     setLoading(false);
  //   }
  // };

  const fetchProfile = async () => {
    try {
      setLoading(true);
      const response = await axios.get(
        `${API_BASE_URL}/profile`,
        getAuthHeaders()
      );
      const userData = response.data.user;

      setUserInfo(userData);
      setEditedInfo(userData);
      setLoading(false);
    } catch (error) {
      console.error("Profile fetch error:", error);
      if (error.response?.status === 403) {
        alert("Session expired. Please login again.");
        localStorage.removeItem("authToken");
        window.location.href = "/login";
      } else {
        showMessage("error", "Failed to load profile");
      }
      setLoading(false);
    }
  };

  const handleEdit = () => {
    setIsEditing(true);
    setEditedInfo({ ...userInfo });
  };

  // const handleSave = async () => {
  //   try {
  //     setUpdating(true);
  //     // Mock axios call
  //     // Replace with: const response = await axios.put(`${API_BASE_URL}/profile`, editedInfo, getAuthHeaders());
  //     const response = { data: { message: 'Profile updated successfully!' } };

  //     setUserInfo({ ...editedInfo });
  //     setIsEditing(false);
  //     showMessage('success', response.data.message);
  //   } catch (error) {
  //     console.error('Profile update error:', error);
  //     showMessage('error', error.response?.data?.error || 'Failed to update profile');
  //   } finally {
  //     setUpdating(false);
  //   }
  // };

  const handleSave = async () => {
    try {
      setUpdating(true);
      const response = await axios.put(
        `${API_BASE_URL}/profile`,
        editedInfo,
        getAuthHeaders()
      );

      setUserInfo({ ...editedInfo });
      setIsEditing(false);
      showMessage("success", response.data.message);
    } catch (error) {
      console.error("Profile update error:", error);
      showMessage(
        "error",
        error.response?.data?.error || "Failed to update profile"
      );
    } finally {
      setUpdating(false);
    }
  };

  const handleCancel = () => {
    setEditedInfo({ ...userInfo });
    setIsEditing(false);
  };

  const handleInputChange = (field, value) => {
    setEditedInfo((prev) => ({
      ...prev,
      [field]: value,
    }));
  };

  // const handleImageUpload = async (event) => {
  //   const file = event.target.files[0];
  //   if (!file) return;

  //   if (file.size > 5 * 1024 * 1024) {
  //     showMessage('error', 'Image size should be less than 5MB');
  //     return;
  //   }

  //   try {
  //     setUploadingImage(true);
  //     const formData = new FormData();
  //     formData.append('profileImage', file);

  //     // Mock response
  //     const response = { data: { imageUrl: URL.createObjectURL(file) } };
  //     const newImageUrl = response.data.imageUrl;

  //     setUserInfo(prev => ({ ...prev, profileImage: newImageUrl }));
  //     setEditedInfo(prev => ({ ...prev, profileImage: newImageUrl }));
  //     showMessage('success', 'Profile image updated successfully!');
  //   } catch (error) {
  //     console.error('Image upload error:', error);
  //     showMessage('error', error.response?.data?.error || 'Failed to upload image');
  //   } finally {
  //     setUploadingImage(false);
  //   }
  // };

  const handleImageUpload = async (event) => {
    const file = event.target.files[0];
    if (!file) return;

    if (file.size > 5 * 1024 * 1024) {
      showMessage("error", "Image size should be less than 5MB");
      return;
    }

    try {
      setUploadingImage(true);
      const formData = new FormData();
      formData.append("profileImage", file);

      const response = await axios.post(
        `${API_BASE_URL}/profile/upload-image`,
        formData,
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("authToken")}`,
            "Content-Type": "multipart/form-data",
          },
        }
      );

      const newImageUrl = response.data.imageUrl;
      setUserInfo((prev) => ({ ...prev, profileImage: newImageUrl }));
      setEditedInfo((prev) => ({ ...prev, profileImage: newImageUrl }));
      showMessage("success", "Profile image updated successfully!");
    } catch (error) {
      console.error("Image upload error:", error);
      showMessage(
        "error",
        error.response?.data?.error || "Failed to upload image"
      );
    } finally {
      setUploadingImage(false);
    }
  };

  // const handleDeleteImage = async () => {
  //   if (!userInfo.profileImage) return;

  //   try {
  //     setUserInfo(prev => ({ ...prev, profileImage: null }));
  //     setEditedInfo(prev => ({ ...prev, profileImage: null }));
  //     showMessage('success', 'Profile image deleted successfully!');
  //   } catch (error) {
  //     console.error('Image delete error:', error);
  //     showMessage('error', 'Failed to delete image');
  //   }
  // };

  const handleDeleteImage = async () => {
    if (!userInfo.profileImage) return;

    try {
      await axios.delete(`${API_BASE_URL}/profile/image`, getAuthHeaders());
      setUserInfo((prev) => ({ ...prev, profileImage: null }));
      setEditedInfo((prev) => ({ ...prev, profileImage: null }));
      showMessage("success", "Profile image deleted successfully!");
    } catch (error) {
      console.error("Image delete error:", error);
      showMessage("error", "Failed to delete image");
    }
  };

  // const handleChangePassword = async () => {
  //   if (passwordData.newPassword !== passwordData.confirmPassword) {
  //     showMessage('error', 'New passwords do not match');
  //     return;
  //   }

  //   if (passwordData.newPassword.length < 6) {
  //     showMessage('error', 'Password must be at least 6 characters long');
  //     return;
  //   }

  //   try {
  //     // Mock response
  //     const response = { data: { message: 'Password changed successfully!' } };

  //     showMessage('success', response.data.message);
  //     setShowPasswordModal(false);
  //     setPasswordData({ currentPassword: '', newPassword: '', confirmPassword: '' });
  //   } catch (error) {
  //     console.error('Password change error:', error);
  //     showMessage('error', error.response?.data?.error || 'Failed to change password');
  //   }
  // };

  const handleChangePassword = async () => {
    if (passwordData.newPassword !== passwordData.confirmPassword) {
      showMessage("error", "New passwords do not match");
      return;
    }

    if (passwordData.newPassword.length < 6) {
      showMessage("error", "Password must be at least 6 characters long");
      return;
    }

    try {
      await axios.put(
        `${API_BASE_URL}/profile/change-password`,
        {
          currentPassword: passwordData.currentPassword,
          newPassword: passwordData.newPassword,
        },
        getAuthHeaders()
      );

      showMessage("success", "Password changed successfully!");
      setShowPasswordModal(false);
      setPasswordData({
        currentPassword: "",
        newPassword: "",
        confirmPassword: "",
      });
    } catch (error) {
      console.error("Password change error:", error);
      showMessage(
        "error",
        error.response?.data?.error || "Failed to change password"
      );
    }
  };

  // const handleDeleteAccount = async () => {
  //   if (!deletePassword) {
  //     showMessage('error', 'Please enter your password to delete account');
  //     return;
  //   }

  //   try {
  //     // Mock response
  //     const response = { data: { message: 'Account deleted successfully!' } };

  //     showMessage('success', response.data.message);
  //     setTimeout(() => {
  //       localStorage.removeItem('authToken');
  //       localStorage.removeItem('userData');
  //       window.location.href = '/login';
  //     }, 2000);
  //   } catch (error) {
  //     console.error('Account deletion error:', error);
  //     showMessage('error', error.response?.data?.error || 'Failed to delete account');
  //   }
  // };

  const handleDeleteAccount = async () => {
    if (!deletePassword) {
      showMessage("error", "Please enter your password to delete account");
      return;
    }

    try {
      await axios.delete(`${API_BASE_URL}/profile/delete-account`, {
        ...getAuthHeaders(),
        data: { password: deletePassword },
      });

      showMessage("success", "Account deleted successfully!");
      setTimeout(() => {
        localStorage.removeItem("authToken");
        localStorage.removeItem("userData");
        window.location.href = "/login";
      }, 2000);
    } catch (error) {
      console.error("Account deletion error:", error);
      showMessage(
        "error",
        error.response?.data?.error || "Failed to delete account"
      );
    }
  };

  const handleLogout = () => {
    if (window.confirm("Are you sure you want to logout?")) {
      localStorage.removeItem("authToken");
      localStorage.removeItem("userData");
      window.location.href = "/login";
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-purple-900 via-blue-900 to-indigo-900 flex items-center justify-center">
        <div className="text-white text-center">
          <div className="animate-spin rounded-full h-16 w-16 border-b-2 border-white mx-auto mb-4"></div>
          <p className="text-xl">Loading your profile...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-900 via-blue-900 to-indigo-900 p-4">
      <div className="max-w-4xl mx-auto">
        {/* Message Display */}
        {message.text && (
          <div
            className={`mb-4 p-4 rounded-lg flex items-center gap-2 ${
              message.type === "success"
                ? "bg-green-500/20 text-green-300 border border-green-500/30"
                : "bg-red-500/20 text-red-300 border border-red-500/30"
            }`}
          >
            {message.type === "success" ? (
              <CheckCircle size={20} />
            ) : (
              <AlertTriangle size={20} />
            )}
            {message.text}
          </div>
        )}

        {/* Header */}
        <div className="bg-white/10 backdrop-blur-md rounded-2xl p-6 mb-6 border border-white/20">
          <div className="flex justify-between items-center">
            <h1 className="text-3xl font-bold text-white flex items-center gap-3">
              <User size={32} className="text-yellow-400" />
              User Profile
            </h1>
            <button
              onClick={handleLogout}
              className="bg-red-500/20 hover:bg-red-500/30 text-red-300 px-4 py-2 rounded-lg transition-colors flex items-center gap-2 border border-red-500/30"
            >
              <LogOut size={18} />
              Logout
            </button>
          </div>
        </div>

        {/* Profile Card */}
        <div className="bg-white/10 backdrop-blur-md rounded-2xl p-8 border border-white/20">
          {/* Profile Header */}
          <div className="flex flex-col md:flex-row items-center gap-6 mb-8 pb-8 border-b border-white/20">
            <div className="relative">
              <div className="w-32 h-32 rounded-full overflow-hidden bg-white/20 flex items-center justify-center border-4 border-white/30">
                {userInfo.profileImage ? (
                  <img
                    src={userInfo.profileImage}
                    alt="Profile"
                    className="w-full h-full object-cover"
                  />
                ) : (
                  <User size={64} className="text-white/50" />
                )}
              </div>

              {isEditing && (
                <div className="absolute -bottom-2 -right-2 flex gap-2">
                  <label className="bg-blue-500 hover:bg-blue-600 text-white p-2 rounded-full cursor-pointer transition-colors">
                    <Camera size={16} />
                    <input
                      type="file"
                      accept="image/*"
                      onChange={handleImageUpload}
                      className="hidden"
                      disabled={uploadingImage}
                    />
                  </label>
                  {userInfo.profileImage && (
                    <button
                      onClick={handleDeleteImage}
                      className="bg-red-500 hover:bg-red-600 text-white p-2 rounded-full transition-colors"
                    >
                      <Trash2 size={16} />
                    </button>
                  )}
                </div>
              )}

              {uploadingImage && (
                <div className="absolute inset-0 bg-black/50 rounded-full flex items-center justify-center">
                  <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-white"></div>
                </div>
              )}
            </div>

            <div className="text-center md:text-left">
              <h2 className="text-2xl font-bold text-white mb-2">
                {userInfo.name || "John Doe"}
              </h2>
              <p className="text-white/70 mb-1">
                {userInfo.email || "john@example.com"}
              </p>
              {userInfo.occupation && (
                <p className="text-yellow-400 font-medium">
                  {userInfo.occupation}
                </p>
              )}
            </div>
          </div>

          {/* Profile Details */}
          <div className="mb-8">
            <div className="flex justify-between items-center mb-6">
              <h3 className="text-xl font-semibold text-white">
                Personal Information
              </h3>
              {!isEditing ? (
                <button
                  onClick={handleEdit}
                  className="bg-blue-500/20 hover:bg-blue-500/30 text-blue-300 px-4 py-2 rounded-lg transition-colors flex items-center gap-2 border border-blue-500/30"
                >
                  <Edit size={18} />
                  Edit Profile
                </button>
              ) : (
                <div className="flex gap-2">
                  <button
                    onClick={handleSave}
                    disabled={updating}
                    className="bg-green-500/20 hover:bg-green-500/30 text-green-300 px-4 py-2 rounded-lg transition-colors flex items-center gap-2 border border-green-500/30 disabled:opacity-50"
                  >
                    <Save size={18} />
                    {updating ? "Saving..." : "Save"}
                  </button>
                  <button
                    onClick={handleCancel}
                    className="bg-gray-500/20 hover:bg-gray-500/30 text-gray-300 px-4 py-2 rounded-lg transition-colors flex items-center gap-2 border border-gray-500/30"
                  >
                    <X size={18} />
                    Cancel
                  </button>
                </div>
              )}
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              {/* Name */}
              <div className="space-y-2">
                <label className="text-white/90 text-sm font-medium flex items-center gap-2">
                  <User size={16} />
                  Full Name
                </label>
                {isEditing ? (
                  <input
                    type="text"
                    value={editedInfo.name || ""}
                    onChange={(e) => handleInputChange("name", e.target.value)}
                    className="w-full bg-white/10 border border-white/30 rounded-lg px-4 py-3 text-white placeholder-white/50 focus:border-yellow-400 focus:outline-none transition-colors"
                  />
                ) : (
                  <p className="text-white/80 bg-white/5 rounded-lg px-4 py-3">
                    {userInfo.name || "Not provided"}
                  </p>
                )}
              </div>

              {/* Email */}
              <div className="space-y-2">
                <label className="text-white/90 text-sm font-medium flex items-center gap-2">
                  <Mail size={16} />
                  Email Address
                </label>
                <p className="text-white/80 bg-white/5 rounded-lg px-4 py-3">
                  {userInfo.email || "Not provided"}
                </p>
              </div>

              {/* Phone */}
              <div className="space-y-2">
                <label className="text-white/90 text-sm font-medium flex items-center gap-2">
                  <Phone size={16} />
                  Phone Number
                </label>
                {isEditing ? (
                  <input
                    type="tel"
                    value={editedInfo.phone || ""}
                    onChange={(e) => handleInputChange("phone", e.target.value)}
                    className="w-full bg-white/10 border border-white/30 rounded-lg px-4 py-3 text-white placeholder-white/50 focus:border-yellow-400 focus:outline-none transition-colors"
                    placeholder="+91 xxx-xxxx-xxxx"
                  />
                ) : (
                  <p className="text-white/80 bg-white/5 rounded-lg px-4 py-3">
                    {userInfo.phone || "Not provided"}
                  </p>
                )}
              </div>

              {/* Date of Birth */}
              <div className="space-y-2">
                <label className="text-white/90 text-sm font-medium flex items-center gap-2">
                  <Calendar size={16} />
                  Date of Birth
                </label>
                {isEditing ? (
                  <input
                    type="date"
                    value={editedInfo.dateOfBirth || ""}
                    onChange={(e) =>
                      handleInputChange("dateOfBirth", e.target.value)
                    }
                    className="w-full bg-white/10 border border-white/30 rounded-lg px-4 py-3 text-white focus:border-yellow-400 focus:outline-none transition-colors"
                  />
                ) : (
                  <p className="text-white/80 bg-white/5 rounded-lg px-4 py-3">
                    {userInfo.dateOfBirth
                      ? new Date(userInfo.dateOfBirth).toLocaleDateString(
                          "en-IN"
                        )
                      : "Not provided"}
                  </p>
                )}
              </div>

              {/* Gender */}
              <div className="space-y-2">
                <label className="text-white/90 text-sm font-medium flex items-center gap-2">
                  <User size={16} />
                  Gender
                </label>
                {isEditing ? (
                  <select
                    value={editedInfo.gender || ""}
                    onChange={(e) =>
                      handleInputChange("gender", e.target.value)
                    }
                    className="w-full bg-white/10 border border-white/30 rounded-lg px-4 py-3 text-white focus:border-yellow-400 focus:outline-none transition-colors"
                  >
                    <option value="">Select Gender</option>
                    <option value="male">Male</option>
                    <option value="female">Female</option>
                    <option value="other">Other</option>
                    <option value="prefer-not-to-say">Prefer not to say</option>
                  </select>
                ) : (
                  <p className="text-white/80 bg-white/5 rounded-lg px-4 py-3 capitalize">
                    {userInfo.gender || "Not provided"}
                  </p>
                )}
              </div>

              {/* Occupation */}
              <div className="space-y-2">
                <label className="text-white/90 text-sm font-medium flex items-center gap-2">
                  <User size={16} />
                  Occupation
                </label>
                {isEditing ? (
                  <input
                    type="text"
                    value={editedInfo.occupation || ""}
                    onChange={(e) =>
                      handleInputChange("occupation", e.target.value)
                    }
                    className="w-full bg-white/10 border border-white/30 rounded-lg px-4 py-3 text-white placeholder-white/50 focus:border-yellow-400 focus:outline-none transition-colors"
                    placeholder="Your occupation"
                  />
                ) : (
                  <p className="text-white/80 bg-white/5 rounded-lg px-4 py-3">
                    {userInfo.occupation || "Not provided"}
                  </p>
                )}
              </div>
            </div>

            {/* Full-width fields */}
            <div className="grid grid-cols-1 gap-6 mt-6">
              {/* Birth Place */}
              <div className="space-y-2">
                <label className="text-white/90 text-sm font-medium flex items-center gap-2">
                  <MapPin size={16} />
                  Birth Place
                </label>
                {isEditing ? (
                  <input
                    type="text"
                    value={editedInfo.birthPlace || ""}
                    onChange={(e) =>
                      handleInputChange("birthPlace", e.target.value)
                    }
                    className="w-full bg-white/10 border border-white/30 rounded-lg px-4 py-3 text-white placeholder-white/50 focus:border-yellow-400 focus:outline-none transition-colors"
                    placeholder="City, State, Country"
                  />
                ) : (
                  <p className="text-white/80 bg-white/5 rounded-lg px-4 py-3">
                    {userInfo.birthPlace || "Not provided"}
                  </p>
                )}
              </div>

              {/* Address */}
              <div className="space-y-2">
                <label className="text-white/90 text-sm font-medium flex items-center gap-2">
                  <MapPin size={16} />
                  Current Address
                </label>
                {isEditing ? (
                  <input
                    type="text"
                    value={editedInfo.address || ""}
                    onChange={(e) =>
                      handleInputChange("address", e.target.value)
                    }
                    className="w-full bg-white/10 border border-white/30 rounded-lg px-4 py-3 text-white placeholder-white/50 focus:border-yellow-400 focus:outline-none transition-colors"
                    placeholder="Your current address"
                  />
                ) : (
                  <p className="text-white/80 bg-white/5 rounded-lg px-4 py-3">
                    {userInfo.address || "Not provided"}
                  </p>
                )}
              </div>

              {/* Bio */}
              <div className="space-y-2">
                <label className="text-white/90 text-sm font-medium">Bio</label>
                {isEditing ? (
                  <textarea
                    value={editedInfo.bio || ""}
                    onChange={(e) => handleInputChange("bio", e.target.value)}
                    rows={3}
                    className="w-full bg-white/10 border border-white/30 rounded-lg px-4 py-3 text-white placeholder-white/50 focus:border-yellow-400 focus:outline-none transition-colors resize-none"
                    placeholder="Tell us about yourself..."
                  />
                ) : (
                  <p className="text-white/80 bg-white/5 rounded-lg px-4 py-3">
                    {userInfo.bio || "Not provided"}
                  </p>
                )}
              </div>
            </div>
          </div>

          {/* Account Actions */}
          <div className="border-t border-white/20 pt-8">
            <h3 className="text-xl font-semibold text-white mb-6">
              Account Actions
            </h3>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <button
                onClick={() => setShowPasswordModal(true)}
                className="bg-blue-500/20 hover:bg-blue-500/30 text-blue-300 px-6 py-3 rounded-lg transition-colors flex items-center justify-center gap-2 border border-blue-500/30"
              >
                <Lock size={18} />
                Change Password
              </button>
              <button
                onClick={() => setShowDeleteModal(true)}
                className="bg-red-500/20 hover:bg-red-500/30 text-red-300 px-6 py-3 rounded-lg transition-colors flex items-center justify-center gap-2 border border-red-500/30"
              >
                <Trash2 size={18} />
                Delete Account
              </button>
            </div>
          </div>
        </div>

        {/* Password Change Modal */}
        {showPasswordModal && (
          <div className="fixed inset-0 bg-black/50 flex items-center justify-center p-4 z-50">
            <div className="bg-white/10 backdrop-blur-md rounded-2xl p-6 w-full max-w-md border border-white/20">
              <h3 className="text-xl font-semibold text-white mb-4">
                Change Password
              </h3>

              <div className="space-y-4">
                <div className="space-y-2">
                  <label className="text-white/90 text-sm">
                    Current Password
                  </label>
                  <div className="relative">
                    <input
                      type={showPassword.current ? "text" : "password"}
                      value={passwordData.currentPassword}
                      onChange={(e) =>
                        setPasswordData({
                          ...passwordData,
                          currentPassword: e.target.value,
                        })
                      }
                      className="w-full bg-white/10 border border-white/30 rounded-lg px-4 py-3 text-white pr-12 focus:border-yellow-400 focus:outline-none transition-colors"
                    />
                    <button
                      type="button"
                      onClick={() =>
                        setShowPassword({
                          ...showPassword,
                          current: !showPassword.current,
                        })
                      }
                      className="absolute right-3 top-1/2 transform -translate-y-1/2 text-white/50 hover:text-white/80"
                    >
                      {showPassword.current ? (
                        <EyeOff size={20} />
                      ) : (
                        <Eye size={20} />
                      )}
                    </button>
                  </div>
                </div>

                <div className="space-y-2">
                  <label className="text-white/90 text-sm">New Password</label>
                  <div className="relative">
                    <input
                      type={showPassword.new ? "text" : "password"}
                      value={passwordData.newPassword}
                      onChange={(e) =>
                        setPasswordData({
                          ...passwordData,
                          newPassword: e.target.value,
                        })
                      }
                      className="w-full bg-white/10 border border-white/30 rounded-lg px-4 py-3 text-white pr-12 focus:border-yellow-400 focus:outline-none transition-colors"
                    />
                    <button
                      type="button"
                      onClick={() =>
                        setShowPassword({
                          ...showPassword,
                          new: !showPassword.new,
                        })
                      }
                      className="absolute right-3 top-1/2 transform -translate-y-1/2 text-white/50 hover:text-white/80"
                    >
                      {showPassword.new ? (
                        <EyeOff size={20} />
                      ) : (
                        <Eye size={20} />
                      )}
                    </button>
                  </div>
                </div>

                <div className="space-y-2">
                  <label className="text-white/90 text-sm">
                    Confirm New Password
                  </label>
                  <div className="relative">
                    <input
                      type={showPassword.confirm ? "text" : "password"}
                      value={passwordData.confirmPassword}
                      onChange={(e) =>
                        setPasswordData({
                          ...passwordData,
                          confirmPassword: e.target.value,
                        })
                      }
                      className="w-full bg-white/10 border border-white/30 rounded-lg px-4 py-3 text-white pr-12 focus:border-yellow-400 focus:outline-none transition-colors"
                    />
                    <button
                      type="button"
                      onClick={() =>
                        setShowPassword({
                          ...showPassword,
                          confirm: !showPassword.confirm,
                        })
                      }
                      className="absolute right-3 top-1/2 transform -translate-y-1/2 text-white/50 hover:text-white/80"
                    >
                      {showPassword.confirm ? (
                        <EyeOff size={20} />
                      ) : (
                        <Eye size={20} />
                      )}
                    </button>
                  </div>
                </div>
              </div>

              <div className="flex gap-3 mt-6">
                <button
                  onClick={handleChangePassword}
                  className="flex-1 bg-blue-500/20 hover:bg-blue-500/30 text-blue-300 px-4 py-2 rounded-lg transition-colors border border-blue-500/30"
                >
                  Change Password
                </button>
                <button
                  onClick={() => {
                    setShowPasswordModal(false);
                    setPasswordData({
                      currentPassword: "",
                      newPassword: "",
                      confirmPassword: "",
                    });
                    setShowPassword({
                      current: false,
                      new: false,
                      confirm: false,
                    });
                  }}
                  className="flex-1 bg-gray-500/20 hover:bg-gray-500/30 text-gray-300 px-4 py-2 rounded-lg transition-colors border border-gray-500/30"
                >
                  Cancel
                </button>
              </div>
            </div>
          </div>
        )}

        {/* Delete Account Modal */}
        {showDeleteModal && (
          <div className="fixed inset-0 bg-black/50 flex items-center justify-center p-4 z-50">
            <div className="bg-white/10 backdrop-blur-md rounded-2xl p-6 w-full max-w-md border border-red-500/30">
              <h3 className="text-xl font-semibold text-red-300 mb-4 flex items-center gap-2">
                <AlertTriangle size={24} />
                Delete Account
              </h3>

              <p className="text-white/80 mb-4">
                This action cannot be undone. This will permanently delete your
                account and all associated data.
              </p>

              <div className="space-y-4">
                <div className="space-y-2">
                  <label className="text-white/90 text-sm">
                    Enter your password to confirm
                  </label>
                  <input
                    type="password"
                    value={deletePassword}
                    onChange={(e) => setDeletePassword(e.target.value)}
                    className="w-full bg-white/10 border border-red-500/30 rounded-lg px-4 py-3 text-white focus:border-red-400 focus:outline-none transition-colors"
                    placeholder="Your password"
                  />
                </div>
              </div>

              <div className="flex gap-3 mt-6">
                <button
                  onClick={handleDeleteAccount}
                  className="flex-1 bg-red-500/20 hover:bg-red-500/30 text-red-300 px-4 py-2 rounded-lg transition-colors border border-red-500/30"
                >
                  Delete Account
                </button>
                <button
                  onClick={() => {
                    setShowDeleteModal(false);
                    setDeletePassword("");
                  }}
                  className="flex-1 bg-gray-500/20 hover:bg-gray-500/30 text-gray-300 px-4 py-2 rounded-lg transition-colors border border-gray-500/30"
                >
                  Cancel
                </button>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default UserProfile;
