import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import "./Navbar.css";

const Navbar = () => {
  const navigate = useNavigate();
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userName, setUserName] = useState('');

  // Check authentication status
  useEffect(() => {
    const checkAuthStatus = () => {
      const token = localStorage.getItem('authToken');
      const userData = localStorage.getItem('userData');

      if (token && userData) {
        setIsLoggedIn(true);
        try {
          const user = JSON.parse(userData);
          setUserName(user.name || user.fullName || 'Mystical Seeker');
        } catch (error) {
          setUserName('Mystical Seeker');
        }
      } else {
        setIsLoggedIn(false);
        setUserName('');
      }
    };

    checkAuthStatus();

    // Listen for storage changes (useful for login/logout across tabs)
    window.addEventListener('storage', checkAuthStatus);

    // Also listen for custom events from login/logout
    window.addEventListener('authChange', checkAuthStatus);

    return () => {
      window.removeEventListener('storage', checkAuthStatus);
      window.removeEventListener('authChange', checkAuthStatus);
    };
  }, []);

  const handleLogout = () => {
    // Clear authentication data
    localStorage.removeItem('authToken');
    localStorage.removeItem('userData');

    // Update state
    setIsLoggedIn(false);
    setUserName('');

    // Dispatch custom event for other components
    window.dispatchEvent(new Event('authChange'));

    // Navigate to home
    navigate('/');

    // Show confirmation
    alert('ðŸŒŸ You have been logged out successfully!');
  };

  return (
    <nav className="navbar">
      <div className="navbar-container">
        <div className="navbar-links">
          <Link to="/" className="nav-link">Home</Link>

          {isLoggedIn ? (
            <>
              <Link to="/profile" className="nav-link">Profile</Link>
              <span className="nav-welcome">Welcome, {userName}</span>
              <button 
                onClick={handleLogout} 
                className="nav-link logout-btn"
              >
                Logout
              </button>
            </>
          ) : (
            <Link to="/login" className="nav-link">SignUp/Login</Link>
          )}

          <Link to="/About" className="nav-link">About us</Link>
          <Link to="/MatchMaking" className="nav-link">Match Making</Link>
          <Link to="/Blog" className="nav-link">Blog</Link>
          <Link to="/ContactUs" className="nav-link">Contact us</Link>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
