import React, { useState, useEffect } from 'react';
import { User, Users, Plus, Edit2, Trash2, Calendar, MapPin, Clock, Save, X, UserCheck } from 'lucide-react';

const UserProfile = () => {
  const [activeTab, setActiveTab] = useState('my-profile');
  const [userProfile, setUserProfile] = useState(null);
  const [familyProfiles, setFamilyProfiles] = useState([]);
  const [isEditing, setIsEditing] = useState(false);
  const [editingProfile, setEditingProfile] = useState(null);
  const [showAddForm, setShowAddForm] = useState(false);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  // Mock API functions - replace with actual API calls
  const api = {
    getUserProfile: async () => {
      // Simulate API delay
      await new Promise(resolve => setTimeout(resolve, 500));
      return {
        id: 1,
        name: 'John Doe',
        email: 'john.doe@email.com',
        dateOfBirth: '1990-05-15',
        birthPlace: 'New York, USA',
        gender: 'Male'
      };
    },
    updateUserProfile: async (data) => {
      await new Promise(resolve => setTimeout(resolve, 500));
      return { ...userProfile, ...data };
    },
    getFamilyProfiles: async () => {
      await new Promise(resolve => setTimeout(resolve, 500));
      return [
        { id: 1, name: 'Jane Doe', dob: '1992-08-20', tim: '14:30', place: 'Los Angeles, CA', created_at: '2024-01-15' },
        { id: 2, name: 'Mike Johnson', dob: '1988-03-10', tim: '09:15', place: 'Chicago, IL', created_at: '2024-01-20' },
        { id: 3, name: 'Sarah Wilson', dob: '1995-11-05', tim: '16:45', place: 'Miami, FL', created_at: '2024-02-01' }
      ];
    },
    addProfile: async (data) => {
      await new Promise(resolve => setTimeout(resolve, 500));
      const newProfile = { ...data, id: Date.now(), created_at: new Date().toISOString() };
      return newProfile;
    },
    updateProfile: async (id, data) => {
      await new Promise(resolve => setTimeout(resolve, 500));
      return { ...data, id };
    },
    deleteProfile: async (id) => {
      await new Promise(resolve => setTimeout(resolve, 500));
      return true;
    }
  };

  useEffect(() => {
    loadData();
  }, []);

  const loadData = async () => {
    try {
      setLoading(true);
      const [userProfileData, familyProfilesData] = await Promise.all([
        api.getUserProfile(),
        api.getFamilyProfiles()
      ]);
      setUserProfile(userProfileData);
      setFamilyProfiles(familyProfilesData);
    } catch (err) {
      setError('Failed to load data');
    } finally {
      setLoading(false);
    }
  };

  const handleUpdateUserProfile = async (data) => {
    try {
      const updatedProfile = await api.updateUserProfile(data);
      setUserProfile(updatedProfile);
      setIsEditing(false);
      setError('');
    } catch (err) {
      setError('Failed to update profile');
    }
  };

  const handleAddProfile = async (data) => {
    try {
      const newProfile = await api.addProfile(data);
      setFamilyProfiles([newProfile, ...familyProfiles]);
      setShowAddForm(false);
      setError('');
    } catch (err) {
      setError('Failed to add profile');
    }
  };

  const handleUpdateProfile = async (id, data) => {
    try {
      const updatedProfile = await api.updateProfile(id, data);
      setFamilyProfiles(familyProfiles.map(p => p.id === id ? updatedProfile : p));
      setEditingProfile(null);
      setError('');
    } catch (err) {
      setError('Failed to update profile');
    }
  };

  const handleDeleteProfile = async (id) => {
    if (window.confirm('Are you sure you want to delete this profile?')) {
      try {
        await api.deleteProfile(id);
        setFamilyProfiles(familyProfiles.filter(p => p.id !== id));
        setError('');
      } catch (err) {
        setError('Failed to delete profile');
      }
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4"></div>
          <p className="text-gray-600">Loading profiles...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <div className="bg-white shadow-sm border-b">
        <div className="max-w-6xl mx-auto px-4 py-6">
          <h1 className="text-3xl font-bold text-gray-900 flex items-center gap-3">
            <UserCheck className="text-blue-600" size={32} />
            Profile Management
          </h1>
          <p className="text-gray-600 mt-2">Manage your personal profile and family members</p>
        </div>
      </div>

      {/* Error Message */}
      {error && (
        <div className="max-w-6xl mx-auto px-4 pt-4">
          <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
            {error}
          </div>
        </div>
      )}

      <div className="max-w-6xl mx-auto px-4 py-8">
        {/* Tab Navigation */}
        <div className="bg-white rounded-lg shadow-sm mb-8">
          <div className="border-b">
            <nav className="flex space-x-8 px-6">
              <button
                onClick={() => setActiveTab('my-profile')}
                className={`py-4 px-2 border-b-2 font-medium text-sm transition-colors ${
                  activeTab === 'my-profile'
                    ? 'border-blue-500 text-blue-600'
                    : 'border-transparent text-gray-500 hover:text-gray-700'
                }`}
              >
                <div className="flex items-center gap-2">
                  <User size={18} />
                  My Profile
                </div>
              </button>
              <button
                onClick={() => setActiveTab('family-profiles')}
                className={`py-4 px-2 border-b-2 font-medium text-sm transition-colors ${
                  activeTab === 'family-profiles'
                    ? 'border-blue-500 text-blue-600'
                    : 'border-transparent text-gray-500 hover:text-gray-700'
                }`}
              >
                <div className="flex items-center gap-2">
                  <Users size={18} />
                  Family & Friends ({familyProfiles.length})
                </div>
              </button>
            </nav>
          </div>

          {/* My Profile Tab */}
          {activeTab === 'my-profile' && (
            <div className="p-6">
              {!isEditing ? (
                <UserProfileView 
                  profile={userProfile} 
                  onEdit={() => setIsEditing(true)}
                />
              ) : (
                <UserProfileEdit 
                  profile={userProfile}
                  onSave={handleUpdateUserProfile}
                  onCancel={() => setIsEditing(false)}
                />
              )}
            </div>
          )}

          {/* Family Profiles Tab */}
          {activeTab === 'family-profiles' && (
            <div className="p-6">
              <div className="flex justify-between items-center mb-6">
                <h2 className="text-xl font-semibold text-gray-900">Family & Friends</h2>
                <button
                  onClick={() => setShowAddForm(true)}
                  className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-lg flex items-center gap-2 transition-colors"
                >
                  <Plus size={18} />
                  Add Profile
                </button>
              </div>

              {showAddForm && (
                <div className="mb-6">
                  <ProfileForm
                    onSubmit={handleAddProfile}
                    onCancel={() => setShowAddForm(false)}
                    title="Add New Profile"
                  />
                </div>
              )}

              <FamilyProfilesList
                profiles={familyProfiles}
                editingProfile={editingProfile}
                onEdit={setEditingProfile}
                onUpdate={handleUpdateProfile}
                onDelete={handleDeleteProfile}
                onCancelEdit={() => setEditingProfile(null)}
              />
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

// User Profile View Component
const UserProfileView = ({ profile, onEdit }) => (
  <div className="space-y-6">
    <div className="flex justify-between items-start">
      <h2 className="text-xl font-semibold text-gray-900">Personal Information</h2>
      <button
        onClick={onEdit}
        className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-lg flex items-center gap-2 transition-colors"
      >
        <Edit2 size={16} />
        Edit Profile
      </button>
    </div>

    <div className="grid md:grid-cols-2 gap-6">
      <ProfileField label="Full Name" value={profile.name} icon={<User size={18} />} />
      <ProfileField label="Email" value={profile.email} icon={<User size={18} />} />
      <ProfileField 
        label="Date of Birth" 
        value={profile.dateOfBirth ? new Date(profile.dateOfBirth).toLocaleDateString() : 'Not set'} 
        icon={<Calendar size={18} />} 
      />
      <ProfileField label="Gender" value={profile.gender || 'Not set'} icon={<User size={18} />} />
      <ProfileField 
        label="Birth Place" 
        value={profile.birthPlace || 'Not set'} 
        icon={<MapPin size={18} />} 
        className="md:col-span-2"
      />
    </div>
  </div>
);

// User Profile Edit Component
const UserProfileEdit = ({ profile, onSave, onCancel }) => {
  const [formData, setFormData] = useState({
    name: profile.name || '',
    dateOfBirth: profile.dateOfBirth || '',
    birthPlace: profile.birthPlace || '',
    gender: profile.gender || ''
  });

  const handleSubmit = () => {
    onSave(formData);
  };

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-start">
        <h2 className="text-xl font-semibold text-gray-900">Edit Personal Information</h2>
        <div className="flex gap-2">
          <button
            type="button"
            onClick={onCancel}
            className="px-4 py-2 text-gray-600 hover:text-gray-800 transition-colors"
          >
            <X size={16} />
          </button>
          <button
            type="button"
            onClick={handleSubmit}
            className="bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded-lg flex items-center gap-2 transition-colors"
          >
            <Save size={16} />
            Save
          </button>
        </div>
      </div>

      <div className="grid md:grid-cols-2 gap-6">
        <FormField
          label="Full Name"
          type="text"
          value={formData.name}
          onChange={(e) => setFormData({...formData, name: e.target.value})}
          required
        />
        <FormField
          label="Date of Birth"
          type="date"
          value={formData.dateOfBirth}
          onChange={(e) => setFormData({...formData, dateOfBirth: e.target.value})}
        />
        <FormField
          label="Gender"
          type="select"
          value={formData.gender}
          onChange={(e) => setFormData({...formData, gender: e.target.value})}
          options={[
            { value: '', label: 'Select Gender' },
            { value: 'Male', label: 'Male' },
            { value: 'Female', label: 'Female' },
            { value: 'Other', label: 'Other' }
          ]}
        />
        <FormField
          label="Birth Place"
          type="text"
          value={formData.birthPlace}
          onChange={(e) => setFormData({...formData, birthPlace: e.target.value})}
          className="md:col-span-2"
        />
      </div>
    </div>
  );
};

// Family Profiles List Component
const FamilyProfilesList = ({ profiles, editingProfile, onEdit, onUpdate, onDelete, onCancelEdit }) => {
  if (profiles.length === 0) {
    return (
      <div className="text-center py-12">
        <Users size={48} className="mx-auto text-gray-400 mb-4" />
        <h3 className="text-lg font-medium text-gray-900 mb-2">No profiles yet</h3>
        <p className="text-gray-500">Add your first family member or friend profile to get started.</p>
      </div>
    );
  }

  return (
    <div className="space-y-4">
      {profiles.map((profile) => (
        <div key={profile.id} className="border rounded-lg p-4 hover:shadow-md transition-shadow">
          {editingProfile === profile.id ? (
            <ProfileForm
              initialData={profile}
              onSubmit={(data) => onUpdate(profile.id, data)}
              onCancel={onCancelEdit}
              title="Edit Profile"
            />
          ) : (
            <div className="flex justify-between items-start">
              <div className="grid md:grid-cols-4 gap-4 flex-1">
                <div>
                  <p className="font-medium text-gray-900">{profile.name}</p>
                  <p className="text-sm text-gray-500">Name</p>
                </div>
                <div>
                  <p className="font-medium text-gray-900 flex items-center gap-1">
                    <Calendar size={16} />
                    {new Date(profile.dob).toLocaleDateString()}
                  </p>
                  <p className="text-sm text-gray-500">Date of Birth</p>
                </div>
                <div>
                  <p className="font-medium text-gray-900 flex items-center gap-1">
                    <Clock size={16} />
                    {profile.tim}
                  </p>
                  <p className="text-sm text-gray-500">Time</p>
                </div>
                <div>
                  <p className="font-medium text-gray-900 flex items-center gap-1">
                    <MapPin size={16} />
                    {profile.place}
                  </p>
                  <p className="text-sm text-gray-500">Place</p>
                </div>
              </div>
              <div className="flex gap-2 ml-4">
                <button
                  onClick={() => onEdit(profile.id)}
                  className="text-blue-600 hover:text-blue-800 p-1 transition-colors"
                >
                  <Edit2 size={16} />
                </button>
                <button
                  onClick={() => onDelete(profile.id)}
                  className="text-red-600 hover:text-red-800 p-1 transition-colors"
                >
                  <Trash2 size={16} />
                </button>
              </div>
            </div>
          )}
        </div>
      ))}
    </div>
  );
};

// Profile Form Component
const ProfileForm = ({ initialData = {}, onSubmit, onCancel, title }) => {
  const [formData, setFormData] = useState({
    name: initialData.name || '',
    dob: initialData.dob || '',
    tim: initialData.tim || '',
    place: initialData.place || ''
  });

  const handleSubmit = () => {
    if (!formData.name || !formData.dob || !formData.tim || !formData.place) {
      alert('Please fill in all fields');
      return;
    }
    onSubmit(formData);
  };

  return (
    <div className="bg-gray-50 rounded-lg p-4">
      <h3 className="text-lg font-medium text-gray-900 mb-4">{title}</h3>
      <div className="space-y-4">
        <div className="grid md:grid-cols-2 gap-4">
          <FormField
            label="Name"
            type="text"
            value={formData.name}
            onChange={(e) => setFormData({...formData, name: e.target.value})}
            required
          />
          <FormField
            label="Date of Birth"
            type="date"
            value={formData.dob}
            onChange={(e) => setFormData({...formData, dob: e.target.value})}
            required
          />
          <FormField
            label="Time of Birth"
            type="time"
            value={formData.tim}
            onChange={(e) => setFormData({...formData, tim: e.target.value})}
            required
          />
          <FormField
            label="Place of Birth"
            type="text"
            value={formData.place}
            onChange={(e) => setFormData({...formData, place: e.target.value})}
            required
          />
        </div>
        <div className="flex justify-end gap-2 pt-4">
          <button
            type="button"
            onClick={onCancel}
            className="px-4 py-2 text-gray-600 hover:text-gray-800 transition-colors"
          >
            Cancel
          </button>
          <button
            type="button"
            onClick={handleSubmit}
            className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-lg flex items-center gap-2 transition-colors"
          >
            <Save size={16} />
            Save
          </button>
        </div>
      </div>
    </div>
  );
};

// Reusable Components
const ProfileField = ({ label, value, icon, className = '' }) => (
  <div className={`${className}`}>
    <label className="block text-sm font-medium text-gray-700 mb-1 flex items-center gap-2">
      {icon}
      {label}
    </label>
    <p className="text-gray-900 bg-gray-50 p-3 rounded-lg">{value}</p>
  </div>
);

const FormField = ({ label, type, value, onChange, required = false, options = [], className = '' }) => (
  <div className={className}>
    <label className="block text-sm font-medium text-gray-700 mb-1">
      {label} {required && <span className="text-red-500">*</span>}
    </label>
    {type === 'select' ? (
      <select
        value={value}
        onChange={onChange}
        required={required}
        className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
      >
        {options.map(option => (
          <option key={option.value} value={option.value}>
            {option.label}
          </option>
        ))}
      </select>
    ) : (
      <input
        type={type}
        value={value}
        onChange={onChange}
        required={required}
        className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
      />
    )}
  </div>
);

export default UserProfile;
