import React, { useState, useEffect } from 'react';
import { User, Users, Plus, Edit2, Trash2, Calendar, MapPin, Clock, Save, X, UserCheck } from 'lucide-react';
import './UserProfile.css';

const UserProfile = () => {
  const [activeTab, setActiveTab] = useState('my-profile');
  const [userProfile, setUserProfile] = useState(null);
  const [familyProfiles, setFamilyProfiles] = useState([]);
  const [isEditing, setIsEditing] = useState(false);
  const [editingProfile, setEditingProfile] = useState(null);
  const [showAddForm, setShowAddForm] = useState(false);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  // Mock API functions - replace with actual API calls
  const api = {
    getUserProfile: async () => {
      // Simulate API delay
      await new Promise(resolve => setTimeout(resolve, 500));
      return {
        id: 1,
        name: 'John Doe',
        email: 'john.doe@email.com',
        dateOfBirth: '1990-05-15',
        birthPlace: 'New York, USA',
        gender: 'Male'
      };
    },
    updateUserProfile: async (data) => {
      await new Promise(resolve => setTimeout(resolve, 500));
      return { ...userProfile, ...data };
    },
    getFamilyProfiles: async () => {
      await new Promise(resolve => setTimeout(resolve, 500));
      return [
        { id: 1, name: 'Jane Doe', dob: '1992-08-20', tim: '14:30', place: 'Los Angeles, CA', created_at: '2024-01-15' },
        { id: 2, name: 'Mike Johnson', dob: '1988-03-10', tim: '09:15', place: 'Chicago, IL', created_at: '2024-01-20' },
        { id: 3, name: 'Sarah Wilson', dob: '1995-11-05', tim: '16:45', place: 'Miami, FL', created_at: '2024-02-01' }
      ];
    },
    addProfile: async (data) => {
      await new Promise(resolve => setTimeout(resolve, 500));
      const newProfile = { ...data, id: Date.now(), created_at: new Date().toISOString() };
      return newProfile;
    },
    updateProfile: async (id, data) => {
      await new Promise(resolve => setTimeout(resolve, 500));
      return { ...data, id };
    },
    deleteProfile: async (id) => {
      await new Promise(resolve => setTimeout(resolve, 500));
      return true;
    }
  };

  useEffect(() => {
    loadData();
  }, []);

  const loadData = async () => {
    try {
      setLoading(true);
      const [userProfileData, familyProfilesData] = await Promise.all([
        api.getUserProfile(),
        api.getFamilyProfiles()
      ]);
      setUserProfile(userProfileData);
      setFamilyProfiles(familyProfilesData);
    } catch (err) {
      setError('Failed to load data');
    } finally {
      setLoading(false);
    }
  };

  const handleUpdateUserProfile = async (data) => {
    try {
      const updatedProfile = await api.updateUserProfile(data);
      setUserProfile(updatedProfile);
      setIsEditing(false);
      setError('');
    } catch (err) {
      setError('Failed to update profile');
    }
  };

  const handleAddProfile = async (data) => {
    try {
      const newProfile = await api.addProfile(data);
      setFamilyProfiles([newProfile, ...familyProfiles]);
      setShowAddForm(false);
      setError('');
    } catch (err) {
      setError('Failed to add profile');
    }
  };

  const handleUpdateProfile = async (id, data) => {
    try {
      const updatedProfile = await api.updateProfile(id, data);
      setFamilyProfiles(familyProfiles.map(p => p.id === id ? updatedProfile : p));
      setEditingProfile(null);
      setError('');
    } catch (err) {
      setError('Failed to update profile');
    }
  };

  const handleDeleteProfile = async (id) => {
    if (window.confirm('Are you sure you want to delete this profile?')) {
      try {
        await api.deleteProfile(id);
        setFamilyProfiles(familyProfiles.filter(p => p.id !== id));
        setError('');
      } catch (err) {
        setError('Failed to delete profile');
      }
    }
  };

  if (loading) {
    return (
      <div className="loading-container">
        <div className="loading-content">
          <div className="loading-spinner"></div>
          <p className="loading-text">Loading profiles...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="user-profile-container">
      {/* Header */}
      <div className="header">
        <div className="header-content">
          <h1 className="header-title">
            <UserCheck className="header-icon" size={32} />
            Profile Management
          </h1>
          <p className="header-subtitle">Manage your personal profile and family members</p>
        </div>
      </div>

      {/* Error Message */}
      {error && (
        <div className="error-container">
          <div className="error-message">
            {error}
          </div>
        </div>
      )}

      <div className="main-content">
        {/* Tab Navigation */}
        <div className="tab-container">
          <div className="tab-navigation">
            <nav className="tab-nav">
              <button
                onClick={() => setActiveTab('my-profile')}
                className={`tab-button ${activeTab === 'my-profile' ? 'tab-active' : 'tab-inactive'}`}
              >
                <div className="tab-content">
                  <User size={18} />
                  My Profile
                </div>
              </button>
              <button
                onClick={() => setActiveTab('family-profiles')}
                className={`tab-button ${activeTab === 'family-profiles' ? 'tab-active' : 'tab-inactive'}`}
              >
                <div className="tab-content">
                  <Users size={18} />
                  Family & Friends ({familyProfiles.length})
                </div>
              </button>
            </nav>
          </div>

          {/* My Profile Tab */}
          {activeTab === 'my-profile' && (
            <div className="tab-panel">
              {!isEditing ? (
                <UserProfileView 
                  profile={userProfile} 
                  onEdit={() => setIsEditing(true)}
                />
              ) : (
                <UserProfileEdit 
                  profile={userProfile}
                  onSave={handleUpdateUserProfile}
                  onCancel={() => setIsEditing(false)}
                />
              )}
            </div>
          )}

          {/* Family Profiles Tab */}
          {activeTab === 'family-profiles' && (
            <div className="tab-panel">
              <div className="family-header">
                <h2 className="family-title">Family & Friends</h2>
                <button
                  onClick={() => setShowAddForm(true)}
                  className="add-button"
                >
                  <Plus size={18} />
                  Add Profile
                </button>
              </div>

              {showAddForm && (
                <div className="add-form-container">
                  <ProfileForm
                    onSubmit={handleAddProfile}
                    onCancel={() => setShowAddForm(false)}
                    title="Add New Profile"
                  />
                </div>
              )}

              <FamilyProfilesList
                profiles={familyProfiles}
                editingProfile={editingProfile}
                onEdit={setEditingProfile}
                onUpdate={handleUpdateProfile}
                onDelete={handleDeleteProfile}
                onCancelEdit={() => setEditingProfile(null)}
              />
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

// User Profile View Component
const UserProfileView = ({ profile, onEdit }) => (
  <div className="profile-view">
    <div className="profile-header">
      <h2 className="profile-title">Personal Information</h2>
      <button
        onClick={onEdit}
        className="edit-button"
      >
        <Edit2 size={16} />
        Edit Profile
      </button>
    </div>

    <div className="profile-fields">
      <ProfileField label="Full Name" value={profile.name} icon={<User size={18} />} />
      <ProfileField label="Email" value={profile.email} icon={<User size={18} />} />
      <ProfileField 
        label="Date of Birth" 
        value={profile.dateOfBirth ? new Date(profile.dateOfBirth).toLocaleDateString() : 'Not set'} 
        icon={<Calendar size={18} />} 
      />
      <ProfileField label="Gender" value={profile.gender || 'Not set'} icon={<User size={18} />} />
      <ProfileField 
        label="Birth Place" 
        value={profile.birthPlace || 'Not set'} 
        icon={<MapPin size={18} />} 
        className="field-full-width"
      />
    </div>
  </div>
);

// User Profile Edit Component
const UserProfileEdit = ({ profile, onSave, onCancel }) => {
  const [formData, setFormData] = useState({
    name: profile.name || '',
    dateOfBirth: profile.dateOfBirth || '',
    birthPlace: profile.birthPlace || '',
    gender: profile.gender || ''
  });

  const handleSubmit = () => {
    onSave(formData);
  };

  return (
    <div className="profile-edit">
      <div className="profile-header">
        <h2 className="profile-title">Edit Personal Information</h2>
        <div className="action-buttons">
          <button
            type="button"
            onClick={onCancel}
            className="cancel-button"
          >
            <X size={16} />
          </button>
          <button
            type="button"
            onClick={handleSubmit}
            className="save-button"
          >
            <Save size={16} />
            Save
          </button>
        </div>
      </div>

      <div className="form-fields">
        <FormField
          label="Full Name"
          type="text"
          value={formData.name}
          onChange={(e) => setFormData({...formData, name: e.target.value})}
          required
        />
        <FormField
          label="Date of Birth"
          type="date"
          value={formData.dateOfBirth}
          onChange={(e) => setFormData({...formData, dateOfBirth: e.target.value})}
        />
        <FormField
          label="Gender"
          type="select"
          value={formData.gender}
          onChange={(e) => setFormData({...formData, gender: e.target.value})}
          options={[
            { value: '', label: 'Select Gender' },
            { value: 'Male', label: 'Male' },
            { value: 'Female', label: 'Female' },
            { value: 'Other', label: 'Other' }
          ]}
        />
        <FormField
          label="Birth Place"
          type="text"
          value={formData.birthPlace}
          onChange={(e) => setFormData({...formData, birthPlace: e.target.value})}
          className="field-full-width"
        />
      </div>
    </div>
  );
};

// Family Profiles List Component
const FamilyProfilesList = ({ profiles, editingProfile, onEdit, onUpdate, onDelete, onCancelEdit }) => {
  if (profiles.length === 0) {
    return (
      <div className="empty-state">
        <Users size={48} className="empty-icon" />
        <h3 className="empty-title">No profiles yet</h3>
        <p className="empty-description">Add your first family member or friend profile to get started.</p>
      </div>
    );
  }

  return (
    <div className="profiles-list">
      {profiles.map((profile) => (
        <div key={profile.id} className="profile-card">
          {editingProfile === profile.id ? (
            <ProfileForm
              initialData={profile}
              onSubmit={(data) => onUpdate(profile.id, data)}
              onCancel={onCancelEdit}
              title="Edit Profile"
            />
          ) : (
            <div className="profile-card-content">
              <div className="profile-info">
                <div className="info-item">
                  <p className="info-value">{profile.name}</p>
                  <p className="info-label">Name</p>
                </div>
                <div className="info-item">
                  <p className="info-value">
                    <Calendar size={16} />
                    {new Date(profile.dob).toLocaleDateString()}
                  </p>
                  <p className="info-label">Date of Birth</p>
                </div>
                <div className="info-item">
                  <p className="info-value">
                    <Clock size={16} />
                    {profile.tim}
                  </p>
                  <p className="info-label">Time</p>
                </div>
                <div className="info-item">
                  <p className="info-value">
                    <MapPin size={16} />
                    {profile.place}
                  </p>
                  <p className="info-label">Place</p>
                </div>
              </div>
              <div className="profile-actions">
                <button
                  onClick={() => onEdit(profile.id)}
                  className="action-edit"
                >
                  <Edit2 size={16} />
                </button>
                <button
                  onClick={() => onDelete(profile.id)}
                  className="action-delete"
                >
                  <Trash2 size={16} />
                </button>
              </div>
            </div>
          )}
        </div>
      ))}
    </div>
  );
};

// Profile Form Component
const ProfileForm = ({ initialData = {}, onSubmit, onCancel, title }) => {
  const [formData, setFormData] = useState({
    name: initialData.name || '',
    dob: initialData.dob || '',
    tim: initialData.tim || '',
    place: initialData.place || ''
  });

  const handleSubmit = () => {
    if (!formData.name || !formData.dob || !formData.tim || !formData.place) {
      alert('Please fill in all fields');
      return;
    }
    onSubmit(formData);
  };

  return (
    <div className="profile-form">
      <h3 className="form-title">{title}</h3>
      <div className="form-content">
        <div className="form-fields">
          <FormField
            label="Name"
            type="text"
            value={formData.name}
            onChange={(e) => setFormData({...formData, name: e.target.value})}
            required
          />
          <FormField
            label="Date of Birth"
            type="date"
            value={formData.dob}
            onChange={(e) => setFormData({...formData, dob: e.target.value})}
            required
          />
          <FormField
            label="Time of Birth"
            type="time"
            value={formData.tim}
            onChange={(e) => setFormData({...formData, tim: e.target.value})}
            required
          />
          <FormField
            label="Place of Birth"
            type="text"
            value={formData.place}
            onChange={(e) => setFormData({...formData, place: e.target.value})}
            required
          />
        </div>
        <div className="form-actions">
          <button
            type="button"
            onClick={onCancel}
            className="cancel-button"
          >
            Cancel
          </button>
          <button
            type="button"
            onClick={handleSubmit}
            className="submit-button"
          >
            <Save size={16} />
            Save
          </button>
        </div>
      </div>
    </div>
  );
};

// Reusable Components
const ProfileField = ({ label, value, icon, className = '' }) => (
  <div className={`profile-field ${className}`}>
    <label className="field-label">
      {icon}
      {label}
    </label>
    <p className="field-value">{value}</p>
  </div>
);

const FormField = ({ label, type, value, onChange, required = false, options = [], className = '' }) => (
  <div className={`form-field ${className}`}>
    <label className="field-label">
      {label} {required && <span className="required">*</span>}
    </label>
    {type === 'select' ? (
      <select
        value={value}
        onChange={onChange}
        required={required}
        className="field-input"
      >
        {options.map(option => (
          <option key={option.value} value={option.value}>
            {option.label}
          </option>
        ))}
      </select>
    ) : (
      <input
        type={type}
        value={value}
        onChange={onChange}
        required={required}
        className="field-input"
      />
    )}
  </div>
);

export default UserProfile;
