import React, { useState, useEffect } from 'react';
import './UserProfile.css';

const UserProfile = () => {
  // State management
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [editing, setEditing] = useState(false);
  const [formData, setFormData] = useState({});
  const [imageFile, setImageFile] = useState(null);
  const [showPasswordModal, setShowPasswordModal] = useState(false);
  const [passwordData, setPasswordData] = useState({
    currentPassword: '',
    newPassword: '',
    confirmPassword: ''
  });

  // Fetch user profile on component mount
  useEffect(() => {
    fetchUserProfile();
  }, []);

  // Fetch user profile data
  const fetchUserProfile = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        window.location.href = '/login';
        return;
      }

      const response = await fetch('http://localhost:4000/profile', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (response.ok) {
        const data = await response.json();
        setUser(data.user);
        setFormData(data.user);
      } else {
        if (response.status === 401) {
          localStorage.removeItem('token');
          window.location.href = '/login';
        } else {
          setError('Failed to load profile');
        }
      }
    } catch (err) {
      setError('Network error');
    } finally {
      setLoading(false);
    }
  };

  // Handle form input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  // Handle image selection
  const handleImageChange = (e) => {
    if (e.target.files[0]) {
      setImageFile(e.target.files[0]);
    }
  };

  // Save profile changes
  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:4000/profile', {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(formData)
      });

      if (response.ok) {
        setUser(formData);
        setEditing(false);
        alert('Profile updated!');

        // Upload image if selected
        if (imageFile) {
          await uploadImage();
        }
      } else {
        const error = await response.json();
        alert(error.error || 'Update failed');
      }
    } catch (err) {
      alert('Error updating profile');
    }
  };

  // Upload profile image
  const uploadImage = async () => {
    const formData = new FormData();
    formData.append('profileImage', imageFile);

    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:4000/profile/upload-image', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`
        },
        body: formData
      });

      if (response.ok) {
        const data = await response.json();
        setUser(prev => ({ ...prev, profileImage: data.imageUrl }));
        setImageFile(null);
      }
    } catch (err) {
      console.error('Image upload failed:', err);
    }
  };

  // Change password
  const handlePasswordChange = async (e) => {
    e.preventDefault();

    if (passwordData.newPassword !== passwordData.confirmPassword) {
      alert("Passwords don't match");
      return;
    }

    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:4000/profile/change-password', {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          currentPassword: passwordData.currentPassword,
          newPassword: passwordData.newPassword
        })
      });

      if (response.ok) {
        alert('Password changed!');
        setShowPasswordModal(false);
        setPasswordData({ currentPassword: '', newPassword: '', confirmPassword: '' });
      } else {
        const error = await response.json();
        alert(error.error || 'Password change failed');
      }
    } catch (err) {
      alert('Error changing password');
    }
  };

  // Logout user
  const handleLogout = () => {
    localStorage.removeItem('token');
    window.location.href = '/login';
  };

  // Loading state
  if (loading) {
    return <div className="loading">Loading profile...</div>;
  }

  // Error state
  if (error) {
    return (
      <div className="error">
        <p>{error}</p>
        <button onClick={fetchUserProfile}>Try Again</button>
      </div>
    );
  }

  // Main profile UI
  return (
    <div className="profile-container">
      {/* Profile Header */}
      <div className="profile-header">
        <h1>User Profile</h1>
        <button onClick={handleLogout} className="logout-btn">
          Logout
        </button>
      </div>

      {/* Profile Info */}
      <div className="profile-info">
        <div className="avatar-section">
          {user.profileImage ? (
            <img src={user.profileImage} alt="Profile" />
          ) : (
            <div className="avatar-placeholder">👤</div>
          )}
          {editing && (
            <input 
              type="file" 
              onChange={handleImageChange} 
              accept="image/*" 
            />
          )}
        </div>

        <div className="user-details">
          <h2>{user.name}</h2>
          <p>{user.email}</p>
        </div>
      </div>

      {/* Edit/Save Button */}
      <button 
        onClick={() => setEditing(!editing)} 
        className="edit-btn"
      >
        {editing ? 'Cancel' : 'Edit Profile'}
      </button>

      {/* Profile Form */}
      {editing ? (
        <form onSubmit={handleSubmit} className="profile-form">
          <div className="form-group">
            <label>Full Name</label>
            <input
              type="text"
              name="name"
              value={formData.name || ''}
              onChange={handleInputChange}
              required
            />
          </div>

          <div className="form-group">
            <label>Phone</label>
            <input
              type="tel"
              name="phone"
              value={formData.phone || ''}
              onChange={handleInputChange}
            />
          </div>

          <div className="form-group">
            <label>Bio</label>
            <textarea
              name="bio"
              value={formData.bio || ''}
              onChange={handleInputChange}
            />
          </div>

          <button type="submit" className="save-btn">
            Save Changes
          </button>
        </form>
      ) : (
        <div className="profile-details">
          <p><strong>Phone:</strong> {user.phone || 'Not provided'}</p>
          <p><strong>Bio:</strong> {user.bio || 'Not provided'}</p>
        </div>
      )}

      {/* Account Actions */}
      <div className="account-actions">
        <button 
          onClick={() => setShowPasswordModal(true)}
          className="password-btn"
        >
          Change Password
        </button>
      </div>

      {/* Password Change Modal */}
      {showPasswordModal && (
        <div className="modal">
          <div className="modal-content">
            <h3>Change Password</h3>
            <form onSubmit={handlePasswordChange}>
              <div className="form-group">
                <label>Current Password</label>
                <input
                  type="password"
                  value={passwordData.currentPassword}
                  onChange={(e) => setPasswordData({
                    ...passwordData,
                    currentPassword: e.target.value
                  })}
                  required
                />
              </div>

              <div className="form-group">
                <label>New Password</label>
                <input
                  type="password"
                  value={passwordData.newPassword}
                  onChange={(e) => setPasswordData({
                    ...passwordData,
                    newPassword: e.target.value
                  })}
                  required
                />
              </div>

              <div className="form-group">
                <label>Confirm Password</label>
                <input
                  type="password"
                  value={passwordData.confirmPassword}
                  onChange={(e) => setPasswordData({
                    ...passwordData,
                    confirmPassword: e.target.value
                  })}
                  required
                />
              </div>

              <div className="modal-actions">
                <button type="submit">Change Password</button>
                <button 
                  type="button" 
                  onClick={() => setShowPasswordModal(false)}
                >
                  Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default UserProfile;
