import React, { useState, useEffect } from 'react';
import './UserProfile2.css';

const UserProfile = () => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [retryCount, setRetryCount] = useState(0);
  const [editing, setEditing] = useState(false);
  const [formData, setFormData] = useState({});
  const [imageFile, setImageFile] = useState(null);
  const [showPasswordModal, setShowPasswordModal] = useState(false);
  const [passwordData, setPasswordData] = useState({
    currentPassword: '',
    newPassword: '',
    confirmPassword: ''
  });
  const [profiles, setProfiles] = useState([]); // For storing multiple profiles
  const [showAddProfileModal, setShowAddProfileModal] = useState(false);
  const [newProfileData, setNewProfileData] = useState({
    name: '',
    relation: '',
    phone: '',
    email: ''
  });

  // ... (keep all your existing useEffect and other functions)

  const fetchProfiles = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:4000/profiles', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (response.ok) {
        const data = await response.json();
        setProfiles(data.profiles);
      }
    } catch (error) {
      console.error('Error fetching profiles:', error);
    }
  };

  const handleAddProfile = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:4000/profiles', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(newProfileData)
      });

      if (response.ok) {
        const data = await response.json();
        setProfiles([...profiles, data.profile]);
        setShowAddProfileModal(false);
        setNewProfileData({
          name: '',
          relation: '',
          phone: '',
          email: ''
        });
      }
    } catch (error) {
      console.error('Error adding profile:', error);
    }
  };

  // Add this to your existing useEffect to fetch profiles when component mounts
  useEffect(() => {
    if (user) {
      fetchProfiles();
    }
  }, [user]);

  // ... (keep all your existing code until the return statement)

  return (
    <div className="profile-container">
      {/* Sidebar Navigation */}
      <div className="profile-sidebar">
        <div className="sidebar-header">
          <h3>Family Profiles</h3>
          <button 
            className="add-profile-btn"
            onClick={() => setShowAddProfileModal(true)}
          >
            + Add Profile
          </button>
        </div>

        <div className="profile-list">
          {/* Main User Profile */}
          <div className="profile-item active">
            <div className="profile-avatar-small">
              {user?.profileImage ? (
                <img src={user.profileImage} alt="Profile" />
              ) : (
                <div className="avatar-placeholder-small">👤</div>
              )}
            </div>
            <div className="profile-info-small">
              <h4>{user?.name}</h4>
              <p>Main Profile</p>
            </div>
          </div>

          {/* Other Profiles */}
          {profiles.map(profile => (
            <div className="profile-item" key={profile.id}>
              <div className="profile-avatar-small">
                {profile.profileImage ? (
                  <img src={profile.profileImage} alt="Profile" />
                ) : (
                  <div className="avatar-placeholder-small">👤</div>
                )}
              </div>
              <div className="profile-info-small">
                <h4>{profile.name}</h4>
                <p>{profile.relation}</p>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Main Content */}
      <div className="profile-main-content">
        {/* Header */}
        <div className="profile-header">
          <div className="profile-title">
            <div className="user-icon">👤</div>
            <h1>User Profile</h1>
          </div>
          <div style={{ display: 'flex', gap: '10px' }}>
            <button className="logout-btn" onClick={handleLogout}>
              🚪 Logout
            </button>
          </div>
        </div>

        {/* Rest of your existing profile content... */}
        {/* ... (keep all your existing profile content here) */}

        {/* Add Profile Modal */}
        {showAddProfileModal && (
          <div className="modal-overlay">
            <div className="modal">
              <div className="modal-header">
                <h3>Add New Profile</h3>
                <button 
                  className="close-btn"
                  onClick={() => setShowAddProfileModal(false)}
                >
                  ✕
                </button>
              </div>
              <form onSubmit={handleAddProfile}>
                <div className="form-group">
                  <label>Full Name</label>
                  <input
                    type="text"
                    value={newProfileData.name}
                    onChange={(e) => setNewProfileData({
                      ...newProfileData,
                      name: e.target.value
                    })}
                    required
                  />
                </div>
                <div className="form-group">
                  <label>Relation</label>
                  <select
                    value={newProfileData.relation}
                    onChange={(e) => setNewProfileData({
                      ...newProfileData,
                      relation: e.target.value
                    })}
                    required
                  >
                    <option value="">Select Relation</option>
                    <option value="Spouse">Spouse</option>
                    <option value="Child">Child</option>
                    <option value="Parent">Parent</option>
                    <option value="Sibling">Sibling</option>
                    <option value="Other">Other</option>
                  </select>
                </div>
                <div className="form-group">
                  <label>Phone Number</label>
                  <input
                    type="tel"
                    value={newProfileData.phone}
                    onChange={(e) => setNewProfileData({
                      ...newProfileData,
                      phone: e.target.value
                    })}
                  />
                </div>
                <div className="form-group">
                  <label>Email</label>
                  <input
                    type="email"
                    value={newProfileData.email}
                    onChange={(e) => setNewProfileData({
                      ...newProfileData,
                      email: e.target.value
                    })}
                  />
                </div>
                <div className="modal-actions">
                  <button type="submit" className="save-btn">
                    Save Profile
                  </button>
                  <button 
                    type="button" 
                    className="cancel-btn"
                    onClick={() => setShowAddProfileModal(false)}
                  >
                    Cancel
                  </button>
                </div>
              </form>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default UserProfile;
