import React, { useState, useEffect } from "react";
import "./UserProfile2.css";

const UserProfile = () => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [editing, setEditing] = useState(false);
  const [formData, setFormData] = useState({});

  // New state for profiles management
  const [profiles, setProfiles] = useState([]);
  const [showAddProfile, setShowAddProfile] = useState(false);
  const [profileFormData, setProfileFormData] = useState({
    name: "",
    relation: "",
    phone: "",
    email: "",
  });
  const [profileLoading, setProfileLoading] = useState(false);

  axios
    .get("http://localhost:4000/profiles", {
      headers: {
        Authorization: `Bearer ${localStorage.getItem("authToken")}`,
      },
    })
    .then((response) => {
      console.log("Profiles: ", response.data);
    })
    .catch((error) => {
      console.error("Error:", error);
    });

  useEffect(() => {
    fetchUserProfile();
    fetchProfiles();
  }, []);

  const fetchUserProfile = async () => {
    try {
      const token = localStorage.getItem("token");

      if (!token) {
        window.location.href = "/login";
        return;
      }

      const response = await fetch("http://localhost:4000/profile", {
        method: "GET",
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "application/json",
        },
      });

      if (response.ok) {
        const data = await response.json();
        setUser(data.user);
        setFormData(data.user);
        setError(null);
      } else {
        if (response.status === 403 || response.status === 401) {
          localStorage.removeItem("token");
          window.location.href = "/login";
        } else {
          setError("Failed to fetch profile");
        }
      }
    } catch (error) {
      setError("Network error occurred");
    } finally {
      setLoading(false);
    }
  };

  // Fetch all profiles for the user
  const fetchProfiles = async () => {
    try {
      const token = localStorage.getItem("token");

      if (!token) return;

      const response = await fetch("http://localhost:4000/profiles", {
        method: "GET",
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "application/json",
        },
      });

      if (response.ok) {
        const data = await response.json();
        setProfiles(data.profiles || []);
      }
    } catch (error) {
      console.error("Failed to fetch profiles:", error);
    }
  };

  // Add new profile
  const handleAddProfile = async (e) => {
    e.preventDefault();
    setProfileLoading(true);

    try {
      const token = localStorage.getItem("token");

      const response = await fetch("http://localhost:4000/profiles", {
        method: "POST",
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "application/json",
        },
        body: JSON.stringify(profileFormData),
      });

      if (response.ok) {
        const data = await response.json();
        setProfiles((prev) => [...prev, data.profile]);
        setProfileFormData({ name: "", relation: "", phone: "", email: "" });
        setShowAddProfile(false);
        alert("Profile added successfully!");
      } else {
        const error = await response.json();
        alert(error.error || "Failed to add profile");
      }
    } catch (err) {
      alert("Something went wrong");
    } finally {
      setProfileLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleProfileInputChange = (e) => {
    const { name, value } = e.target;
    setProfileFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const token = localStorage.getItem("token");

    try {
      const response = await fetch("http://localhost:4000/profile/save", {
        method: "POST",
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData),
      });

      if (response.ok) {
        setUser(formData);
        setEditing(false);
        alert("Profile saved successfully!");
      } else {
        const error = await response.json();
        alert(error.error || "Failed to save profile");
      }
    } catch (err) {
      alert("Something went wrong");
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    window.location.href = "/login";
  };

  if (loading) {
    return (
      <div className="loading-container">
        <div className="loading-card">
          <div className="spinner"></div>
          <div className="loading-text">Loading profile...</div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="error-container">
        <div className="error-card">
          <div className="error-icon">❌</div>
          <h3 className="error-title">Error: {error}</h3>
          <button
            onClick={() => {
              setError(null);
              setLoading(true);
              fetchUserProfile();
            }}
            className="retry-btn"
          >
            Try Again
          </button>
        </div>
      </div>
    );
  }

  if (!user) {
    return (
      <div className="error-container">
        <div className="error-card">
          <div className="error-icon">😕</div>
          <div className="error-title">Failed to load profile</div>
          <button
            onClick={() => window.location.reload()}
            className="retry-btn"
          >
            Reload Page
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="profile-page">
      {/* Navigation */}
      <nav className="navbar">
        <div className="nav-container">
          <div className="nav-left">
            <div className="logo">NumPath</div>
          </div>
          <div className="nav-right">
            <span className="welcome-text">Welcome, {user.name}</span>
            <button className="logout-btn" onClick={handleLogout}>
              🚪 Logout
            </button>
          </div>
        </div>
      </nav>

      <div className="main-container">
        <div className="profile-layout">
          {/* Left Side - Add Profile Section */}
          <div className="left-sidebar">
            <div className="add-profile-section">
              <h3 className="sidebar-title">
                <span className="sidebar-icon">👥</span>
                Manage Profiles
              </h3>

              <button
                className="add-profile-btn"
                onClick={() => setShowAddProfile(!showAddProfile)}
              >
                ➕ Add New Profile
              </button>

              {showAddProfile && (
                <div className="add-profile-form">
                  <form onSubmit={handleAddProfile}>
                    <div className="form-group">
                      <input
                        type="text"
                        name="name"
                        placeholder="Full Name"
                        value={profileFormData.name}
                        onChange={handleProfileInputChange}
                        required
                        className="form-input small"
                      />
                    </div>

                    <div className="form-group">
                      <input
                        type="text"
                        name="relation"
                        placeholder="Relation (e.g., Father, Mother)"
                        value={profileFormData.relation}
                        onChange={handleProfileInputChange}
                        required
                        className="form-input small"
                      />
                    </div>

                    <div className="form-group">
                      <input
                        type="tel"
                        name="phone"
                        placeholder="Phone Number"
                        value={profileFormData.phone}
                        onChange={handleProfileInputChange}
                        className="form-input small"
                      />
                    </div>

                    <div className="form-group">
                      <input
                        type="email"
                        name="email"
                        placeholder="Email Address"
                        value={profileFormData.email}
                        onChange={handleProfileInputChange}
                        className="form-input small"
                      />
                    </div>

                    <div className="form-actions small">
                      <button
                        type="submit"
                        className="save-btn small"
                        disabled={profileLoading}
                      >
                        {profileLoading ? "💾 Saving..." : "💾 Save Profile"}
                      </button>
                      <button
                        type="button"
                        className="cancel-btn small"
                        onClick={() => setShowAddProfile(false)}
                      >
                        ❌ Cancel
                      </button>
                    </div>
                  </form>
                </div>
              )}

              {/* Display Saved Profiles */}
              <div className="saved-profiles">
                <h4 className="saved-profiles-title">
                  Saved Profiles ({profiles.length})
                </h4>
                {profiles.length === 0 ? (
                  <p className="no-profiles">No profiles added yet</p>
                ) : (
                  <div className="profiles-list">
                    {profiles.map((profile) => (
                      <div key={profile.id} className="profile-item">
                        <div className="profile-item-icon">👤</div>
                        <div className="profile-item-details">
                          <div className="profile-item-name">
                            {profile.name}
                          </div>
                          <div className="profile-item-relation">
                            {profile.relation}
                          </div>
                          {profile.phone && (
                            <div className="profile-item-contact">
                              📱 {profile.phone}
                            </div>
                          )}
                          {profile.email && (
                            <div className="profile-item-contact">
                              📧 {profile.email}
                            </div>
                          )}
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            </div>
          </div>

          {/* Right Side - Main Profile Content */}
          <div className="right-content">
            {/* Profile Header */}
            <div className="profile-header-card">
              <div className="profile-bg"></div>
              <div className="profile-content">
                <div className="profile-avatar">
                  <div className="avatar-icon">👤</div>
                </div>
                <h1 className="profile-name">{user.name}</h1>
                <p className="profile-email">{user.email}</p>
                <button
                  className={`edit-toggle-btn ${editing ? "cancel" : "edit"}`}
                  onClick={() => setEditing(!editing)}
                >
                  {editing ? "❌ Cancel Edit" : "✏️ Edit Profile"}
                </button>
              </div>
            </div>

            {/* Profile Form */}
            <div className="profile-form-card">
              <h2 className="form-title">
                <span className="form-icon">📋</span>
                Personal Information
              </h2>

              <form onSubmit={handleSubmit} className="profile-form">
                <div className="form-grid">
                  <div className="form-group">
                    <label className="form-label">👤 Full Name</label>
                    <input
                      type="text"
                      name="name"
                      value={formData.name || ""}
                      onChange={handleInputChange}
                      disabled={!editing}
                      className={`form-input ${
                        editing ? "editable" : "disabled"
                      }`}
                    />
                  </div>

                  <div className="form-group">
                    <label className="form-label">📧 Email Address</label>
                    <input
                      type="email"
                      value={user.email}
                      disabled
                      className="form-input disabled"
                    />
                  </div>

                  <div className="form-group">
                    <label className="form-label">📱 Phone Number</label>
                    <input
                      type="tel"
                      name="phone"
                      value={formData.phone || ""}
                      onChange={handleInputChange}
                      disabled={!editing}
                      placeholder="+91 xxxxx-xxxxx"
                      className={`form-input ${
                        editing ? "editable" : "disabled"
                      }`}
                    />
                  </div>

                  <div className="form-group">
                    <label className="form-label">📅 Date of Birth</label>
                    <input
                      type="date"
                      name="dateOfBirth"
                      value={
                        formData.dateOfBirth
                          ? formData.dateOfBirth.split("T")[0]
                          : ""
                      }
                      onChange={handleInputChange}
                      disabled={!editing}
                      className={`form-input ${
                        editing ? "editable" : "disabled"
                      }`}
                    />
                  </div>

                  <div className="form-group full-width">
                    <label className="form-label">🏠 Address</label>
                    <textarea
                      name="address"
                      value={formData.address || ""}
                      onChange={handleInputChange}
                      disabled={!editing}
                      rows="3"
                      placeholder="Your address"
                      className={`form-textarea ${
                        editing ? "editable" : "disabled"
                      }`}
                    />
                  </div>

                  <div className="form-group">
                    <label className="form-label">💼 Occupation</label>
                    <input
                      type="text"
                      name="occupation"
                      value={formData.occupation || ""}
                      onChange={handleInputChange}
                      disabled={!editing}
                      placeholder="Your occupation"
                      className={`form-input ${
                        editing ? "editable" : "disabled"
                      }`}
                    />
                  </div>

                  <div className="form-group">
                    <label className="form-label">📝 Bio</label>
                    <textarea
                      name="bio"
                      value={formData.bio || ""}
                      onChange={handleInputChange}
                      disabled={!editing}
                      rows="4"
                      placeholder="Tell us about yourself"
                      className={`form-textarea ${
                        editing ? "editable" : "disabled"
                      }`}
                    />
                  </div>
                </div>

                {editing && (
                  <div className="form-actions">
                    <button type="submit" className="save-btn">
                      💾 Save Changes
                    </button>
                  </div>
                )}
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default UserProfile;
