// UserProfile.js
import React, { useState, useEffect } from 'react';
import { User, Users, Plus, Edit2, Trash2, Calendar, MapPin, Clock, Save, X, UserCheck } from 'lucide-react';
import './UserProfile.css';

const UserProfile = () => {
  const [activeTab, setActiveTab] = useState('my-profile');
  const [userProfile, setUserProfile] = useState(null);
  const [familyProfiles, setFamilyProfiles] = useState([]);
  const [isEditing, setIsEditing] = useState(false);
  const [editingProfile, setEditingProfile] = useState(null);
  const [showAddForm, setShowAddForm] = useState(false);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  // Mock API functions - replace with actual API calls
  const api = {
    getUserProfile: async () => {
      // Simulate API delay
      await new Promise(resolve => setTimeout(resolve, 500));
      return {
        id: 1,
        name: 'enter your name',
        email: 'john.doe@email.com',
        dateOfBirth: '1990-05-15',
        birthPlace: 'New York, USA',
        gender: 'Male'
      };
    },
    updateUserProfile: async (data) => {
      await new Promise(resolve => setTimeout(resolve, 500));
      return { ...userProfile, ...data };
    },
    getFamilyProfiles: async () => {
      await new Promise(resolve => setTimeout(resolve, 500));
      return [
        { id: 1, name: 'Jane Doe', dob: '1992-08-20', tim: '14:30', place: 'Los Angeles, CA', created_at: '2024-01-15' },
        { id: 2, name: 'Mike Johnson', dob: '1988-03-10', tim: '09:15', place: 'Chicago, IL', created_at: '2024-01-20' },
        { id: 3, name: 'Sarah Wilson', dob: '1995-11-05', tim: '16:45', place: 'Miami, FL', created_at: '2024-02-01' }
      ];
    },
    addProfile: async (data) => {
      await new Promise(resolve => setTimeout(resolve, 500));
      const newProfile = { ...data, id: Date.now(), created_at: new Date().toISOString() };
      return newProfile;
    },
    updateProfile: async (id, data) => {
      await new Promise(resolve => setTimeout(resolve, 500));
      return { ...data, id };
    },
    deleteProfile: async (id) => {
      await new Promise(resolve => setTimeout(resolve, 500));
      return true;
    }
  };

  useEffect(() => {
    loadData();
  }, []);

  const loadData = async () => {
    try {
      setLoading(true);
      const [userProfileData, familyProfilesData] = await Promise.all([
        api.getUserProfile(),
        api.getFamilyProfiles()
      ]);
      setUserProfile(userProfileData);
      setFamilyProfiles(familyProfilesData);
    } catch (err) {
      setError('Failed to load data');
    } finally {
      setLoading(false);
    }
  };

  const handleUpdateUserProfile = async (data) => {
    try {
      const updatedProfile = await api.updateUserProfile(data);
      setUserProfile(updatedProfile);
      setIsEditing(false);
      setError('');
    } catch (err) {
      setError('Failed to update profile');
    }
  };

  const handleAddProfile = async (data) => {
    try {
      const newProfile = await api.addProfile(data);
      setFamilyProfiles([newProfile, ...familyProfiles]);
      setShowAddForm(false);
      setError('');
    } catch (err) {
      setError('Failed to add profile');
    }
  };

  const handleUpdateProfile = async (id, data) => {
    try {
      const updatedProfile = await api.updateProfile(id, data);
      setFamilyProfiles(familyProfiles.map(p => p.id === id ? updatedProfile : p));
      setEditingProfile(null);
      setError('');
    } catch (err) {
      setError('Failed to update profile');
    }
  };

  const handleDeleteProfile = async (id) => {
    if (window.confirm('Are you sure you want to delete this profile?')) {
      try {
        await api.deleteProfile(id);
        setFamilyProfiles(familyProfiles.filter(p => p.id !== id));
        setError('');
      } catch (err) {
        setError('Failed to delete profile');
      }
    }
  };

  if (loading) {
    return (
      <div className="loading-container">
        <div className="loading-content">
          <div className="loading-spinner"></div>
          <p className="loading-text">Loading profiles...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="user-profile">
      {/* Header */}
      <div className="profile-header">
        <div className="profile-header-content">
          <h1 className="profile-title">
            <UserCheck className="title-icon" size={32} />
            Profile Management
          </h1>
          <p className="profile-subtitle">Manage your personal profile and family members</p>
        </div>
      </div>

      {/* Error Message */}
      {error && (
        <div className="profile-container">
          <div className="error-message">
            {error}
          </div>
        </div>
      )}

      <div className="profile-container">
        {/* Tab Navigation */}
        <div className="tab-container">
          <div className="tab-navigation">
            <nav className="tab-nav-list">
              <button
                onClick={() => setActiveTab('my-profile')}
                className={`tab-button ${
                  activeTab === 'my-profile' ? 'tab-button-active' : 'tab-button-inactive'
                }`}
              >
                <div className="tab-button-content">
                  <User size={18} />
                  My Profile
                </div>
              </button>
              <button
                onClick={() => setActiveTab('family-profiles')}
                className={`tab-button ${
                  activeTab === 'family-profiles' ? 'tab-button-active' : 'tab-button-inactive'
                }`}
              >
                <div className="tab-button-content">
                  <Users size={18} />
                  Family & Friends ({familyProfiles.length})
                </div>
              </button>
            </nav>
          </div>

          {/* My Profile Tab */}
          {activeTab === 'my-profile' && (
            <div className="tab-content">
              {!isEditing ? (
                <UserProfileView 
                  profile={userProfile} 
                  onEdit={() => setIsEditing(true)}
                />
              ) : (
                <UserProfileEdit 
                  profile={userProfile}
                  onSave={handleUpdateUserProfile}
                  onCancel={() => setIsEditing(false)}
                />
              )}
            </div>
          )}

          {/* Family Profiles Tab */}
          {activeTab === 'family-profiles' && (
            <div className="tab-content">
              <div className="family-header">
                <h2 className="section-title">Family & Friends</h2>
                <button
                  onClick={() => setShowAddForm(true)}
                  className="btn-primary"
                >
                  <Plus size={18} />
                  Add Profile
                </button>
              </div>

              {showAddForm && (
                <ProfileForm
                  onSubmit={handleAddProfile}
                  onCancel={() => setShowAddForm(false)}
                  title="Add New Profile"
                />
              )}

              <FamilyProfilesList
                profiles={familyProfiles}
                editingProfile={editingProfile}
                onEdit={setEditingProfile}
                onUpdate={handleUpdateProfile}
                onDelete={handleDeleteProfile}
                onCancelEdit={() => setEditingProfile(null)}
              />
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

// User Profile View Component
const UserProfileView = ({ profile, onEdit }) => (
  <div>
    <div className="profile-section-header">
      <h2 className="section-title">Personal Information</h2>
      <button onClick={onEdit} className="btn-primary">
        <Edit2 size={16} />
        Edit Profile
      </button>
    </div>

    <div className="profile-grid">
      <ProfileField label="Full Name" value={profile.name} icon={<User size={18} />} />
      <ProfileField label="Email" value={profile.email} icon={<User size={18} />} />
      <ProfileField 
        label="Date of Birth" 
        value={profile.dateOfBirth ? new Date(profile.dateOfBirth).toLocaleDateString() : 'Not set'} 
        icon={<Calendar size={18} />} 
      />
      <ProfileField label="Gender" value={profile.gender || 'Not set'} icon={<User size={18} />} />
      <ProfileField 
        label="Birth Place" 
        value={profile.birthPlace || 'Not set'} 
        icon={<MapPin size={18} />} 
        className="md-col-span-2"
      />
    </div>
  </div>
);

// User Profile Edit Component
const UserProfileEdit = ({ profile, onSave, onCancel }) => {
  const [formData, setFormData] = useState({
    name: profile.name || '',
    dateOfBirth: profile.dateOfBirth || '',
    birthPlace: profile.birthPlace || '',
    gender: profile.gender || ''
  });

  const handleSubmit = () => {
    onSave(formData);
  };

  return (
    <div>
      <div className="profile-section-header">
        <h2 className="section-title">Edit Personal Information</h2>
        <div style={{ display: 'flex', gap: '0.5rem' }}>
          <button type="button" onClick={onCancel} className="btn-secondary">
            <X size={16} />
          </button>
          <button type="button" onClick={handleSubmit} className="btn-success">
            <Save size={16} />
            Save
          </button>
        </div>
      </div>

      <div className="profile-grid">
        <FormField
          label="Full Name"
          type="text"
          value={formData.name}
          onChange={(e) => setFormData({...formData, name: e.target.value})}
          required
        />
        <FormField
          label="Date of Birth"
          type="date"
          value={formData.dateOfBirth}
          onChange={(e) => setFormData({...formData, dateOfBirth: e.target.value})}
        />
        <FormField
          label="Gender"
          type="select"
          value={formData.gender}
          onChange={(e) => setFormData({...formData, gender: e.target.value})}
          options={[
            { value: '', label: 'Select Gender' },
            { value: 'Male', label: 'Male' },
            { value: 'Female', label: 'Female' },
            { value: 'Other', label: 'Other' }
          ]}
        />
        <FormField
          label="Birth Place"
          type="text"
          value={formData.birthPlace}
          onChange={(e) => setFormData({...formData, birthPlace: e.target.value})}
          className="md-col-span-2"
        />
      </div>
    </div>
  );
};

// Family Profiles List Component
const FamilyProfilesList = ({ profiles, editingProfile, onEdit, onUpdate, onDelete, onCancelEdit }) => {
  if (profiles.length === 0) {
    return (
      <div className="empty-state">
        <Users size={48} className="empty-state-icon" />
        <h3 className="empty-state-title">No profiles yet</h3>
        <p className="empty-state-description">Add your first family member or friend profile to get started.</p>
      </div>
    );
  }

  return (
    <div className="profiles-list">
      {profiles.map((profile) => (
        <div key={profile.id} className="profile-card">
          {editingProfile === profile.id ? (
            <ProfileForm
              initialData={profile}
              onSubmit={(data) => onUpdate(profile.id, data)}
              onCancel={onCancelEdit}
              title="Edit Profile"
            />
          ) : (
            <div className="profile-card-content">
              <div className="profile-card-info">
                <div className="profile-card-field">
                  <p className="profile-card-value">{profile.name}</p>
                  <p className="profile-card-label">Name</p>
                </div>
                <div className="profile-card-field">
                  <p className="profile-card-value">
                    <Calendar size={16} />
                    {new Date(profile.dob).toLocaleDateString()}
                  </p>
                  <p className="profile-card-label">Date of Birth</p>
                </div>
                <div className="profile-card-field">
                  <p className="profile-card-value">
                    <Clock size={16} />
                    {profile.tim}
                  </p>
                  <p className="profile-card-label">Time</p>
                </div>
                <div className="profile-card-field">
                  <p className="profile-card-value">
                    <MapPin size={16} />
                    {profile.place}
                  </p>
                  <p className="profile-card-label">Place</p>
                </div>
              </div>
              <div className="profile-card-actions">
                <button
                  onClick={() => onEdit(profile.id)}
                  className="btn-icon btn-icon-blue"
                >
                  <Edit2 size={16} />
                </button>
                <button
                  onClick={() => onDelete(profile.id)}
                  className="btn-icon btn-icon-red"
                >
                  <Trash2 size={16} />
                </button>
              </div>
            </div>
          )}
        </div>
      ))}
    </div>
  );
};

// Profile Form Component
const ProfileForm = ({ initialData = {}, onSubmit, onCancel, title }) => {
  const [formData, setFormData] = useState({
    name: initialData.name || '',
    dob: initialData.dob || '',
    tim: initialData.tim || '',
    place: initialData.place || ''
  });

  const handleSubmit = () => {
    if (!formData.name || !formData.dob || !formData.tim || !formData.place) {
      alert('Please fill in all fields');
      return;
    }
    onSubmit(formData);
  };

  return (
    <div className="form-container">
      <h3 className="form-title">{title}</h3>
      <div>
        <div className="form-grid">
          <FormField
            label="Name"
            type="text"
            value={formData.name}
            onChange={(e) => setFormData({...formData, name: e.target.value})}
            required
          />
          <FormField
            label="Date of Birth"
            type="date"
            value={formData.dob}
            onChange={(e) => setFormData({...formData, dob: e.target.value})}
            required
          />
          <FormField
            label="Time of Birth"
            type="time"
            value={formData.tim}
            onChange={(e) => setFormData({...formData, tim: e.target.value})}
            required
          />
          <FormField
            label="Place of Birth"
            type="text"
            value={formData.place}
            onChange={(e) => setFormData({...formData, place: e.target.value})}
            required
          />
        </div>
        <div className="form-actions">
          <button type="button" onClick={onCancel} className="btn-secondary">
            Cancel
          </button>
          <button type="button" onClick={handleSubmit} className="btn-primary">
            <Save size={16} />
            Save
          </button>
        </div>
      </div>
    </div>
  );
};

// Reusable Components
const ProfileField = ({ label, value, icon, className = '' }) => (
  <div className={`form-field ${className}`}>
    <label className="form-field-label form-field-label-with-icon">
      {icon}
      {label}
    </label>
    <p className="profile-field-display">{value}</p>
  </div>
);

const FormField = ({ label, type, value, onChange, required = false, options = [], className = '' }) => (
  <div className={`form-field ${className}`}>
    <label className="form-field-label">
      {label} {required && <span className="form-field-required">*</span>}
    </label>
    {type === 'select' ? (
      <select
        value={value}
        onChange={onChange}
        required={required}
        className="form-select"
      >
        {options.map(option => (
          <option key={option.value} value={option.value}>
            {option.label}
          </option>
        ))}
      </select>
    ) : (
      <input
        type={type}
        value={value}
        onChange={onChange}
        required={required}
        className="form-input"
      />
    )}
  </div>
);

export default UserProfile;
