import React, { useState, useEffect } from 'react';
import './UserProfile2.css'; // Import the CSS file

const UserProfile = () => {
  // State variables to manage data
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [user, setUser] = useState(null);
  const [profiles, setProfiles] = useState([]);
  const [isEditing, setIsEditing] = useState(false);
  const [showAddForm, setShowAddForm] = useState(false);

  // Form data for adding new profile
  const [newProfile, setNewProfile] = useState({
    name: '',
    dob: '',
    tim: '',
    place: ''
  });

  // Form data for editing existing profile
  const [editData, setEditData] = useState({
    name: '',
    email: '',
    dob: ''
  });

  // Load user data when component starts
  useEffect(() => {
    loadUserData();
  }, []);

  // Function to load user data (replace with actual API calls)
  const loadUserData = async () => {
    setIsLoading(true);
    try {
      // Simulate loading time
      await new Promise(resolve => setTimeout(resolve, 1500));

      // Set mock user data (replace with actual API call)
      const userData = {
        name: 'John Doe',
        email: 'john.doe@example.com'
      };
      setUser(userData);
      setEditData({
        name: userData.name,
        email: userData.email,
        dob: '1990-05-15'
      });

      // Set mock profiles data (replace with actual API call)
      setProfiles([
        {
          id: 1,
          name: 'Alice Johnson',
          dob: '1990-05-15',
          tim: '14:30',
          place: 'New York'
        },
        {
          id: 2,
          name: 'Bob Smith',
          dob: '1985-12-03',
          tim: '09:15',
          place: 'California'
        }
      ]);

      setIsLoading(false);
    } catch (err) {
      setError('Failed to load data');
      setIsLoading(false);
    }
  };

  // Handle adding new profile
  const handleAddProfile = async (e) => {
    e.preventDefault();

    // Check if all fields are filled
    if (!newProfile.name || !newProfile.dob || !newProfile.tim || !newProfile.place) {
      alert('Please fill all fields');
      return;
    }

    try {
      // Add to profiles list (replace with actual API call)
      const newProfileWithId = {
        id: Date.now(), // Simple ID generation
        ...newProfile
      };

      setProfiles([...profiles, newProfileWithId]);

      // Reset form
      setNewProfile({ name: '', dob: '', tim: '', place: '' });
      setShowAddForm(false);

      alert('Profile added successfully!');
    } catch (err) {
      alert('Failed to add profile');
    }
  };

  // Handle saving user profile changes
  const handleSaveProfile = async (e) => {
    e.preventDefault();

    try {
      // Update user data (replace with actual API call)
      setUser({
        name: editData.name,
        email: editData.email
      });

      setIsEditing(false);
      alert('Profile updated successfully!');
    } catch (err) {
      alert('Failed to update profile');
    }
  };

  // Handle logout
  const handleLogout = () => {
    const confirmLogout = window.confirm('Are you sure you want to logout?');
    if (confirmLogout) {
      setUser(null);
      setProfiles([]);
      // Add actual logout logic here
    }
  };

  // Handle retry when error occurs
  const handleRetry = () => {
    setError(null);
    loadUserData();
  };

  // Show loading screen
  if (isLoading) {
    return (
      <div className="loading-container">
        <div className="loading-card">
          <div className="spinner"></div>
          <div className="loading-text">Loading your profile...</div>
        </div>
      </div>
    );
  }

  // Show error screen
  if (error) {
    return (
      <div className="error-container">
        <div className="error-card">
          <div className="error-icon">⚠️</div>
          <div className="error-title">Something went wrong</div>
          <button className="retry-btn" onClick={handleRetry}>
            Try Again
          </button>
        </div>
      </div>
    );
  }

  // Main profile page
  return (
    <div className="profile-page">
      {/* Top Navigation Bar */}
      <nav className="navbar">
        <div className="nav-container">
          <div className="nav-left">
            <div className="logo">ProfileHub</div>
          </div>
          <div className="nav-right">
            <span className="welcome-text">Welcome, {user?.name}!</span>
            <button className="logout-btn" onClick={handleLogout}>
              Logout
            </button>
          </div>
        </div>
      </nav>

      {/* Main Content Area */}
      <div className="main-container">
        <div className="profile-layout">

          {/* Left Sidebar - Add New Profile */}
          <div className="left-sidebar">
            <div className="add-profile-section">
              <div className="sidebar-title">
                <span className="sidebar-icon">👤</span>
                Add New Profile
              </div>

              <button 
                className="add-profile-btn"
                onClick={() => setShowAddForm(!showAddForm)}
              >
                {showAddForm ? 'Cancel' : 'Add New Profile'}
              </button>

              {/* Add Profile Form */}
              {showAddForm && (
                <form className="add-profile-form" onSubmit={handleAddProfile}>
                  <input
                    type="text"
                    placeholder="Full Name"
                    className="form-input small"
                    value={newProfile.name}
                    onChange={(e) => setNewProfile({...newProfile, name: e.target.value})}
                  />
                  <input
                    type="date"
                    className="form-input small"
                    value={newProfile.dob}
                    onChange={(e) => setNewProfile({...newProfile, dob: e.target.value})}
                  />
                  <input
                    type="time"
                    className="form-input small"
                    value={newProfile.tim}
                    onChange={(e) => setNewProfile({...newProfile, tim: e.target.value})}
                  />
                  <input
                    type="text"
                    placeholder="Place"
                    className="form-input small"
                    value={newProfile.place}
                    onChange={(e) => setNewProfile({...newProfile, place: e.target.value})}
                  />
                  <div className="form-actions small">
                    <button type="submit" className="save-btn small">
                      Save
                    </button>
                    <button 
                      type="button" 
                      className="cancel-btn small"
                      onClick={() => setShowAddForm(false)}
                    >
                      Cancel
                    </button>
                  </div>
                </form>
              )}

              {/* Saved Profiles List */}
              <div className="saved-profiles">
                <div className="saved-profiles-title">Saved Profiles</div>
                {profiles.length === 0 ? (
                  <div className="no-profiles">No profiles saved yet</div>
                ) : (
                  <div className="profiles-list">
                    {profiles.map((profile) => (
                      <div key={profile.id} className="profile-item">
                        <div className="profile-item-icon">👤</div>
                        <div className="profile-item-details">
                          <div className="profile-item-name">{profile.name}</div>
                          <div className="profile-item-relation">Contact</div>
                          <div className="profile-item-contact">📅 {profile.dob}</div>
                          <div className="profile-item-contact">🕐 {profile.tim}</div>
                          <div className="profile-item-contact">📍 {profile.place}</div>
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            </div>
          </div>

          {/* Right Content Area */}
          <div className="right-content">

            {/* User Profile Header */}
            <div className="profile-header-card">
              <div className="profile-bg"></div>
              <div className="profile-content">
                <div className="profile-avatar">
                  <span className="avatar-icon">👤</span>
                </div>
                <h1 className="profile-name">{user?.name}</h1>
                <p className="profile-email">{user?.email}</p>
                <button 
                  className={`edit-toggle-btn ${isEditing ? 'cancel' : 'edit'}`}
                  onClick={() => setIsEditing(!isEditing)}
                >
                  {isEditing ? 'Cancel Edit' : 'Edit Profile'}
                </button>
              </div>
            </div>

            {/* Profile Edit Form */}
            <div className="profile-form-card">
              <div className="form-title">
                <span className="form-icon">✏️</span>
                {isEditing ? 'Edit Profile' : 'Profile Information'}
              </div>

              <form className="profile-form" onSubmit={handleSaveProfile}>
                <div className="form-grid">
                  <div className="form-group">
                    <label className="form-label">Full Name</label>
                    <input
                      type="text"
                      className={`form-input ${isEditing ? 'editable' : 'disabled'}`}
                      value={editData.name}
                      onChange={(e) => setEditData({...editData, name: e.target.value})}
                      disabled={!isEditing}
                    />
                  </div>

                  <div className="form-group">
                    <label className="form-label">Email Address</label>
                    <input
                      type="email"
                      className={`form-input ${isEditing ? 'editable' : 'disabled'}`}
                      value={editData.email}
                      onChange={(e) => setEditData({...editData, email: e.target.value})}
                      disabled={!isEditing}
                    />
                  </div>

                  <div className="form-group">
                    <label className="form-label">Date of Birth</label>
                    <input
                      type="date"
                      className={`form-input ${isEditing ? 'editable' : 'disabled'}`}
                      value={editData.dob}
                      onChange={(e) => setEditData({...editData, dob: e.target.value})}
                      disabled={!isEditing}
                    />
                  </div>
                </div>

                {isEditing && (
                  <div className="form-actions">
                    <button type="submit" className="save-btn">
                      Save Changes
                    </button>
                  </div>
                )}
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default UserProfile;
