import React, { useState } from "react";
import { Routes, Route, useNavigate } from "react-router-dom";
import Home from "./Home";
import Result from "./Result";
import Registration from "./Registration";
import MatchMaking from "./MatchMaking";
import axios from 'axios';
// import ProtectedRoute from "./ProtectedRoute"; 

/**
 * Main App Component with routing and shared state for form
 */
const App = () => {
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    name: "",
    day: "",
    month: "",
    year: "",
  });

  // Handle input change
  const handleInputChange = (field, value) => {
    setFormData((prev) => ({ ...prev, [field]: value }));
  };

  // Handle form submit with login check and API call
  const handleSubmit = async () => {
    console.log("Form submit triggered");

    // Check if user is logged in
    const token = localStorage.getItem("token");
    if (!token) {
      console.log("No token found, user not logged in");
      // Don't navigate to result page
      // Let the Home component handle showing login message
      return;
    }

    // Validate form data
    const { name, day, month, year } = formData;
    if (!name || !day || !month || !year) {
      alert("❌ Please fill in all fields");
      return;
    }

    console.log("User is logged in, proceeding with API call");
    console.log("Form data:", formData);

    // Check if server is running first
    try {
      console.log("Testing server connection...");
      const testResponse = await axios.get("http://localhost:4000");
      console.log("Server test response:", testResponse.data);
    } catch (testError) {
      console.error("Server is not running:", testError);
      alert("❌ Server is not running. Please start the backend server first.\n\nRun: node server.js");
      return;
    }

    // Prepare data for API
    const month_padded = String(month).padStart(2, '0');
    const day_padded = String(day).padStart(2, '0');
    const dob = `${year}-${month_padded}-${day_padded}`;

    const payload = {
      full_name: name,
      dob: dob
    };

    try {
      console.log("Making API call with payload:", payload);

      const response = await axios.post("http://localhost:4000/submit", payload, {
        headers: {
          'Content-Type': 'application/json'
        }
      });

      console.log("API Response:", response);

      if (response.data) {
        console.log("Success! Navigating to result page");
        alert("✅ " + (response.data.message || "Data saved successfully"));
        // Navigate to result page only after successful API call
        navigate("/result");
      }
    } catch (error) {
      console.error("API Error:", error);
      if (error.response) {
        alert(`❌ Server error: ${error.response.data.error || error.response.data.message}`);
      } else if (error.request) {
        alert("❌ Cannot connect to server. Make sure backend is running on port 4000");
      } else {
        alert("❌ Network error: " + error.message);
      }
    }
  };

  // Reset form and navigate back to Home
  const handleReset = () => {
    setFormData({
      name: "",
      day: "",
      month: "",
      year: "",
    });
    navigate("/");
  };

  return (
    <Routes>
      <Route
        path="/"
        element={
          <Home
            formData={formData}
            onInputChange={handleInputChange}
            onSubmit={handleSubmit}
          />
        }
      />
      <Route
        path="/result"
        element={<Result formData={formData} onReset={handleReset} />}
      />
      <Route path="/login" element={<Registration />} />
      <Route path="/matchmaking" element={<MatchMaking />} />
    </Routes>
  );
};

export default App;
