import React from 'react';
import { Heart, Star, Crown, Zap, ArrowLeft } from 'lucide-react';
import './Result.css';

export default function ResultPage({ malePartner, femalePartner, onReset }) {
  // Generate random compatibility score between 70-100
  const compatibilityScore = Math.floor(Math.random() * 30) + 70;

  // Random insights
  const insights = [
    "Your birth dates reveal harmonious energies",
    "Deep emotional connection detected",
    "Excellent communication potential",
    "Shared dreams align beautifully",
    "Natural understanding flows between you",
    "Cosmic forces favor your union"
  ];

  const getScoreMessage = (score) => {
    if (score >= 90) return "Perfect Cosmic Match!";
    if (score >= 80) return "Excellent Compatibility!";
    if (score >= 70) return "Great Connection!";
    return "Good Compatibility!";
  };

  return (
    <div className="result-container">
      {/* Animated Background */}
      <div className="result-background">
        <div className="golden-circle circle-1"></div>
        <div className="golden-circle circle-2"></div>
        <div className="golden-circle circle-3"></div>
        <div className="golden-circle circle-4"></div>
      </div>

      <div className="result-content">
        {/* Header */}
        <div className="result-header">
          <Crown className="crown-icon" />
          <h1 className="result-title">Cosmic Compatibility Report</h1>
          <p className="result-subtitle">
            {malePartner.fullName} & {femalePartner.fullName}
          </p>
        </div>

        {/* Main Result Card */}
        <div className="result-card">
          {/* Score Section */}
          <div className="score-section">
            <div className="star-emoji">✨</div>
            <div className="score-display">
              <h2 className="score-number">{compatibilityScore}%</h2>
              <p className="score-message">{getScoreMessage(compatibilityScore)}</p>
            </div>
          </div>

          {/* Insights Section */}
          <div className="insights-section">
            <div className="insights-header">
              <Star className="star-icon" />
              <h3>Mystical Insights</h3>
            </div>
            <div className="insights-grid">
              {insights.slice(0, 4).map((insight, index) => (
                <div key={index} className="insight-item">
                  <div className="insight-icon">⭐</div>
                  <p>{insight}</p>
                </div>
              ))}
            </div>
          </div>

          {/* Special Message */}
          <div className="message-section">
            <Zap className="zap-icon" />
            <h4>The Universe Has Spoken!</h4>
            <p>
              Your souls dance to the same cosmic rhythm. This connection transcends 
              the physical realm and touches the very essence of your spiritual beings. 
              The celestial bodies have aligned to bring you together in perfect harmony.
            </p>
          </div>

          {/* Action Buttons */}
          <div className="action-buttons">
            <button className="back-btn" onClick={onReset}>
              <ArrowLeft className="btn-icon" />
              Calculate Another Match
            </button>
            <button className="share-btn">
              <Heart className="btn-icon" />
              Share Result
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}
